// src/components/Login/Auth.js
import React from "react";
import { signInWithPopup, signOut } from "firebase/auth";
import { auth, googleProvider } from "../../service/firebaseConfig";
import { useNavigate } from "react-router-dom";

const Auth = () => {
  const navigate = useNavigate();

  const signInWithGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const token = await result.user.getIdToken();
      localStorage.setItem("firebaseToken", token);
      navigate("/protected");
    } catch (error) {
      console.error("Error en el inicio de sesión con Google:", error);
    }
  };

  const logout = async () => {
    try {
      await signOut(auth);
      localStorage.removeItem("firebaseToken");
      navigate("/");
    } catch (error) {
      console.error("Error al cerrar sesión:", error);
    }
  };

  return (
    <div>
      <h1>Login</h1>
      <button onClick={signInWithGoogle} className="btn btn-outline-primary mx-2">
        Iniciar sesión con Google
      </button>
      <button onClick={logout} className="btn btn-outline-terciary mx-2">
        Cerrar sesión
      </button>
    </div>
  );
};

export default Auth;
