import React from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, googleProvider } from '../service/firebaseConfig';
import { useAuth } from '../../context/AuthContext';

const Auth = () => {
  const { setUser, setUserRole } = useAuth();
  const navigate = useNavigate();

  const handleGoogleLogin = async () => {
    try {
      const result = await auth.signInWithPopup(googleProvider);
      const user = result.user;

      const adminEmails = ["guillermo.ibanezc@gmail.com"];
      const role = adminEmails.includes(user.email) ? 'admin' : 'user';

      setUser(user);
      setUserRole(role);

      navigate('/');
    } catch (error) {
      console.error("Error al iniciar sesi贸n con Google:", error);
    }
  };

  return (
    <div className="login-container">
      <h2>Iniciar sesi贸n</h2>
      <button onClick={handleGoogleLogin} className="btn btn-primary">Iniciar sesi贸n con Google</button>
    </div>
  );
};

export default Auth;
