// AuthContext.js
import React, { createContext, useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { onAuthStateChanged, signInWithPopup, signOut } from 'firebase/auth';
import { auth, googleProvider } from '../components/service/firebaseConfig';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

const adminEmails = ["guillermo.ibanezc@gmail.com", "trek0.88@gmail.com"];

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Configurar interceptor global de Axios
  useEffect(() => {
    const interceptor = axios.interceptors.request.use(
      async (config) => {
        if (auth.currentUser) {
          const token = await auth.currentUser.getIdToken();
          config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
      },
      (error) => {
        return Promise.reject(error);
      }
    );

    return () => axios.interceptors.request.eject(interceptor);
  }, []);

  const fetchUserRoleAndMySQLId = async (firebaseUser) => {
    if (!firebaseUser) return null;

    try {
      // Verificar si el usuario ya está registrado en la base de datos MySQL
      let client;
      try {
        const response = await axios.get(`http://localhost:3001/api/clients/email/${firebaseUser.email}`);
        client = response.data;
      } catch (error) {
        // Si el cliente no existe, crear uno nuevo
        if (error.response && error.response.status === 404) {
          const newClient = await axios.post('http://localhost:3001/api/clients', {
            name: firebaseUser.displayName,
            email: firebaseUser.email,
            cuit: '', // Se puede dejar vacío inicialmente
          });
          client = newClient.data;
        } else {
          throw error;
        }
      }

      const isAdmin = adminEmails.includes(firebaseUser.email);

      // Guardar el rol y el MySQL ID en el objeto de usuario
      return {
        ...firebaseUser,
        role: isAdmin ? 'admin' : 'user',
        isAdmin,
        mysqlId: client.id,
      };
    } catch (error) {
      console.error('Error al obtener el rol del usuario o registrar en MySQL:', error);
      return null;
    }
  };

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (firebaseUser) => {
      setLoading(true);
      if (firebaseUser) {
        const userWithRole = await fetchUserRoleAndMySQLId(firebaseUser);
        setUser(userWithRole);
      } else {
        setUser(null);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  const loginWithGoogle = async () => {
    try {
      setLoading(true);
      const result = await signInWithPopup(auth, googleProvider);
      const userWithRole = await fetchUserRoleAndMySQLId(result.user);
      setUser(userWithRole);
      navigate('/');
    } catch (error) {
      console.error('Error al iniciar sesión con Google:', error);
    } finally {
      setLoading(false);
    }
  };

  const logout = async () => {
    try {
      setLoading(true);
      await signOut(auth);
      setUser(null);
      navigate('/');
    } catch (error) {
      console.error('Error al cerrar sesión:', error);
    } finally {
      setLoading(false);
    }
  };

  const value = {
    user,
    loading,
    loginWithGoogle,
    logout,
    isAdmin: user?.isAdmin || false,
    mysqlId: user?.mysqlId || null,
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
