import React, { useState, useEffect } from 'react';
import axios from '../service/axiosConfig';

const PurchaseForm = ({ supplierId, onSave }) => {
  const [supplier, setSupplier] = useState(null);
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [quantity, setQuantity] = useState(0);
  const [price, setPrice] = useState(0);

  useEffect(() => {
    // Fetch supplier details
    axios.get(`/suppliers/${supplierId}`)
      .then((res) => setSupplier(res.data))
      .catch((error) => console.error('Error fetching supplier:', error));

    // Fetch products from this supplier's category
    axios.get(`/products?category=${supplier.category}`)
      .then((res) => setProducts(res.data))
      .catch((error) => console.error('Error fetching products:', error));
  }, [supplierId]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({
      supplierId,
      productId: selectedProduct.id,
      quantity,
      price
    });
  };

  return (
    <div>
      <h2>Compra de Productos - Proveedor: {supplier?.name}</h2>
      <form onSubmit={handleSubmit}>
        <label>Producto:</label>
        <select
          value={selectedProduct?.id || ''}
          onChange={(e) => {
            const product = products.find(p => p.id === e.target.value);
            setSelectedProduct(product);
            setPrice(product?.price || 0);
          }}
        >
          <option value="">Selecciona un Producto</option>
          {products.map((product) => (
            <option key={product.id} value={product.id}>
              {product.title}
            </option>
          ))}
        </select>
        <label>Cantidad:</label>
        <input
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
        />
        <label>Precio:</label>
        <input
          type="number"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
        />
        <button type="submit">Guardar Compra</button>
      </form>
    </div>
  );
};

export default PurchaseForm;
