import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { Table, Button, Spinner } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const ClientesList = () => {
  const [clientes, setClientes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { auth } = useAuth();

  useEffect(() => {
    const fetchClientes = async () => {
      setLoading(true);
      setError(null);
      
      try {
        // Verificar autenticación
        console.log("Estado de auth:", auth);
        console.log("Estado de auth.currentUser:", auth.currentUser);

        if (auth.currentUser) {
          const token = await auth.currentUser.getIdToken();
          console.log("Token obtenido:", token);

          const response = await axios.get('http://localhost:3001/api/clients', {
            headers: { Authorization: `Bearer ${token}` }
          });

          console.log("Respuesta completa:", response);
          
          if (response.data && Array.isArray(response.data)) {
            setClientes(response.data);
            console.log("Clientes establecidos:", response.data);
          } else {
            console.error("La respuesta no contiene un array:", response.data);
            setError("Formato de respuesta incorrecto");
          }
        } else {
          console.error("Usuario no autenticado");
          setError("Usuario no autenticado");
        }
      } catch (error) {
        console.error('Error completo:', error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchClientes();
  }, [auth]);

  if (loading) {
    return (
      <div className="d-flex justify-content-center">
        <Spinner animation="border" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="alert alert-danger">
        Error: {error}
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <h2>Lista de Clientes</h2>
      <Link to="/clients/new" className="btn btn-primary mb-3">
        Nuevo Cliente
      </Link>
      
      {clientes.length === 0 ? (
        <div className="alert alert-info">
          No hay clientes para mostrar
        </div>
      ) : (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>Email</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {clientes.map(cliente => (
              <tr key={cliente.id}>
                <td>{cliente.id}</td>
                <td>{cliente.name}</td>
                <td>{cliente.email}</td>
                <td>
                  <Link 
                    to={`/clients/edit/${cliente.id}`} 
                    className="btn btn-warning me-2"
                  >
                    Editar
                  </Link>
                  <Button 
                    variant="danger" 
                    onClick={() => handleDeleteCliente(cliente.id)}
                  >
                    Eliminar
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </div>
  );
};

export default ClientesList;
