// context/AuthContext.js
import React, { createContext, useContext, useState, useEffect } from 'react';
import { signInWithPopup, signOut } from 'firebase/auth';
import { auth, googleProvider } from '../components/service/firebaseConfig';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [userRole, setUserRole] = useState(null);


  useEffect(() => {
    const token = localStorage.getItem('firebaseToken');
    if (token) {
      // Obtener datos del usuario y rol desde el backend
      axios.get('http://localhost:3001/auth/user', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      }).then(response => {
        setUser(response.data.usuario);
        setIsAdmin(response.data.usuario.esAdmin);
      }).catch(() => {
        logout();
      });
    }
  }, []);

  const loginWithGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const firebaseToken = result.user.accessToken;

      const response = await axios.post('http://localhost:3001/auth/login', { firebaseToken });
      localStorage.setItem('firebaseToken', firebaseToken);
      setUser(response.data.usuario);
      setIsAdmin(response.data.usuario.esAdmin);
    } catch (error) {
      console.error('Error al iniciar sesión con Google:', error);
    }
  };

  const logout = async () => {
    await signOut(auth);
    localStorage.removeItem('firebaseToken');
    setUser(null);
    setIsAdmin(false);
  };

  return (
    <AuthContext.Provider value={{ user, setUser, userRole, setUserRole }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
