import React, { useState, useEffect } from 'react';

const EditItemForm = ({ initialData, onSave }) => {
    const [title, setTitle] = useState(initialData.title);
    const [category, setCategory] = useState(initialData.category);
    const [price, setPrice] = useState(initialData.price);
    const [description, setDescription] = useState(initialData.description);
    const [stock, setStock] = useState(initialData.stock);


    useEffect(() => {
        setTitle(initialData.title);
        setCategory(initialData.category);
        setPrice(initialData.price);
        setDescription(initialData.description);
        setStock(initialData.stock);
    }, [initialData]);

    const handleSubmit = (e) => {
        e.preventDefault();
        const updatedProduct = { title, category, price, description, stock};
        onSave(updatedProduct);
    };

    return (
        <Form onSubmit={handleSubmit}>
            <Row className='justify-content-center'>
                <FormGroup>
                    <Form.Label>Nombre</Form.Label>
                    <Form.Control input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                </FormGroup>
                <FormGroup>
                    <Form.Label>Categoría</Form.Label>
                    <FormSelect
                        value={category}
                        onChange={(e) => setCategory(e.target.value)}
                        autoComplete='category'
                        required
                    >
                        <option value="">Seleccionar categoría</option>
                        <option value="Notebook">Notebook</option>
                        <option value="Celular">Celular</option>
                        <option value="Tablet">Tablet</option>
                    </FormSelect>
                </FormGroup>
            </Row>
                <Row className='justify-content-center'>
                <FormGroup>
                    <Form.Label>Descripción</Form.Label>
                    <Form.Control type="text" value={description} onChange={(e) => setDescription(e.target.value)} />
                </FormGroup>
            </Row>
            <Row className='justify-content-center'>
                <FormGroup>
                    <Form.Label>Stock</Form.Label>
                    <Form.Control type="text" value={stock} onChange={(e) => setStock(e.target.value)} />
                </FormGroup>
                <FormGroup>
                    <Form.Label>Precio</Form.Label>
                    <Form.Control type="text" value={price} onChange={(e) => setPrice(e.target.value)} />
                </FormGroup>
            </Row>
            <Button type="submit">Guardar Cambios</Button>
        </Form>
    );
};

export default EditItemForm;
