import React from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const StoreLocationMap = () => {
  // Configuración del contenedor del mapa
  const containerStyle = {
    width: '100%',
    height: '400px'
  };

  // Ubicación de la tienda (puedes cambiarla según la ubicación real)
  const storeLocation = {
    lat: -34.603722, // Latitud
    lng: -58.381592  // Longitud
  };

  // API Key de Google Maps (usa una variable de entorno para mayor seguridad)
  const GOOGLE_MAPS_API_KEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

  return (
    <LoadScript googleMapsApiKey={GOOGLE_MAPS_API_KEY}>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={storeLocation}
        zoom={15}
      >
        {/* Marcador en la ubicación de la tienda */}
        <Marker position={storeLocation} />
      </GoogleMap>
    </LoadScript>
  );
};

export default StoreLocationMap;
