import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import { Table, Button, Spinner } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const ClientesList = () => {
  const [clientes, setClientes] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user, auth } = useAuth(); // Obtener auth también del contexto

  useEffect(() => {
    const fetchClientes = async () => {
      // Verifica si el usuario está autenticado antes de intentar obtener los clientes
      if (!auth || !auth.currentUser) {
        console.error('Usuario no autenticado o user es null.');
        setLoading(false);
        return;
      }

      setLoading(true);

      try {
        const token = await auth.currentUser.getIdToken();
        console.log('Token obtenido:', token); // Log para verificar si se obtiene el token

        const response = await axios.get('http://localhost:3001/api/clients', {
          headers: { Authorization: `Bearer ${token}` },
        });

        console.log('Clientes obtenidos:', response.data); // Log para verificar la respuesta del servidor
        setClientes(response.data);
      } catch (error) {
        console.error('Error fetching clients:', error);
      } finally {
        setLoading(false);
      }
    };

    // Si el usuario está autenticado, intenta obtener los clientes
    if (auth && auth.currentUser) {
      fetchClientes();
    }
  }, [auth]); // Se sigue observando `auth` para detectar cambios

  const handleDeleteCliente = async (id) => {
    if (!auth || !auth.currentUser) {
      console.error('Usuario no autenticado o user es null.');
      return;
    }

    if (window.confirm('¿Estás seguro de que deseas eliminar este cliente?')) {
      setLoading(true);
      try {
        const token = await auth.currentUser.getIdToken();
        await axios.delete(`http://localhost:3001/api/clients/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Actualizar la lista después de eliminar
        setClientes((prevClientes) => prevClientes.filter((cliente) => cliente.id !== id));
      } catch (error) {
        console.error('Error deleting client:', error);
      } finally {
        setLoading(false);
      }
    }
  };

  if (loading) {
    return <Spinner animation="border" />;
  }

  return (
    <div className="container mt-4">
      <h2>Lista de Clientes</h2>
      <Link to="/form/client" className="btn btn-primary mb-3">Nuevo Cliente</Link>
      {clientes.length === 0 ? (
        <p>No hay clientes disponibles.</p>
      ) : (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>CUIT</th>
              <th>Email</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {clientes.map((cliente) => (
              <tr key={cliente.id}>
                <td>{cliente.id}</td>
                <td>{cliente.name}</td>
                <td>{cliente.cuit}</td>
                <td>{cliente.email}</td>
                <td>
                  <Link to={`/form/client/${cliente.id}`} className="btn btn-warning me-2">
                    Editar
                  </Link>
                  <Button variant="danger" onClick={() => handleDeleteCliente(cliente.id)}>
                    Eliminar
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </div>
  );
};

export default ClientesList;
