import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext'; 
import { Table, Button, Spinner } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import {user} from '../service/firebaseConfig';

const ClientesList = () => {
  const [clientes, setClientes] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const { auth } = useAuth(); // Obtener `auth` directamente del contexto

  useEffect(() => {
    const fetchClientes = async () => {
      setLoading(true);
      try {
        if (user && typeof user.getIdToken === 'function') {
          const token = await user.getIdToken();
          const response = await axios.get('http://localhost:3001/api/clients', {
            headers: { Authorization: `Bearer ${token}` }
          });
          console.log('Clientes obtenidos:', response.data); // Verificar clientes en frontend
          setClientes(response.data);
        } else {
          console.error('Usuario no autenticado o user es null.');
        }
      } catch (error) {
        console.error('Error fetching clients:', error);
      } finally {
        setLoading(false);
      }
    };
  
    fetchClientes();
  }, [user]);
  

  const handleDeleteCliente = async (id) => {
    if (window.confirm('¿Estás seguro de que deseas eliminar este cliente?')) {
      setLoading(true);
      try {
        if (auth && auth.currentUser) {
          const token = await auth.currentUser.getIdToken();
          await axios.delete(`http://localhost:3001/api/clients/${id}`, {
            headers: { Authorization: `Bearer ${token}` }
          });
          // Actualizar la lista después de eliminar
          setClientes((prevClientes) => prevClientes.filter(cliente => cliente.id !== id));
        } else {
          console.error("Error: no se puede eliminar el cliente porque el usuario no está autenticado.");
        }
      } catch (error) {
        console.error('Error deleting client:', error);
      } finally {
        setLoading(false);
      }
    }
  };

  if (loading) {
    return <Spinner animation="border" />;
  }

  return (
    <div>
      <h1>Lista de Clientes</h1>
      <Button className="btn btn-primary mb-3" onClick={() => navigate('/form/client')}>Agregar Cliente</Button>
      {clientes.length === 0 ? (
        <p>No hay clientes disponibles.</p>
      ) : (
        clientes.map((cliente) => (
          <div key={cliente.id}>
            <p>Nombre: {cliente.name}</p>
            <p>CUIT: {cliente.cuit}</p>
            <p>Email: {cliente.email}</p>
            <Button className='btn-warning mx-2' onClick={() => navigate(`/form/client/${cliente.id}`)}>Editar</Button>
            <Button className='btn-danger' onClick={() => handleDeleteCliente(cliente.id)}>Eliminar</Button>
            <hr />
          </div>
        ))
      )}
    </div>
  );
  
};

export default ClientesList;
