import { useState, useContext } from "react";
import { Link } from "react-router-dom";
import { CartContext } from "../../context/cartContext";
import Button from "react-bootstrap/Button";
import { updateProduct } from "../../asyncMock"; // Asegúrate de importar la función updateProduct

const ItemDetail = ({ id, category, title, description, price, img, stock, onEdit }) => {
  const [quantityAdded, setQuantityAdded] = useState(false);
  const [isEditing, setIsEditing] = useState(false); // Estado para controlar el modo de edición
  const [editedProduct, setEditedProduct] = useState({
    id,
    category,
    title,
    description,
    price,
    img,
    stock,
  });
  const { addItem } = useContext(CartContext);

  const handleOnAdd = (quantity) => {
    setQuantityAdded(true);
    const product = { id, title, price, img };
    addItem(product, quantity);
  };

  const handleEditSave = () => {
    // Lógica para guardar el producto editado
    updateProduct(editedProduct.id, editedProduct)
      .then((updatedProduct) => {
        // Lógica adicional después de actualizar el producto, por ejemplo, cerrar el modo de edición
        console.log("Producto actualizado:", updatedProduct);
        setIsEditing(false); // Desactivar el modo de edición después de guardar
      })
      .catch((error) => {
        console.error("Error al actualizar el producto:", error);
      });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedProduct({
      ...editedProduct,
      [name]: value,
    });
  };

  const handleCancelEdit = () => {
    // Lógica para cancelar la edición y volver al modo de visualización
    setEditedProduct({
      id,
      category,
      title,
      description,
      price,
      img,
      stock,
    });
    setIsEditing(false);
  };

  if (isEditing) {
    return (
      <div className="card shadow-lg" style={{ width: 1170, margin: "30px auto" }}>
        <h1 className="text-uppercase p-2">
          Editar Producto
        </h1>
        <form className="p-3">
          <div className="mb-3">
            <label htmlFor="title" className="form-label">Título</label>
            <input type="text" className="form-control" id="title" name="title" value={editedProduct.title} onChange={handleInputChange} />
          </div>
          <div className="mb-3">
            <label htmlFor="description" className="form-label">Descripción</label>
            <textarea className="form-control" id="description" name="description" value={editedProduct.description} onChange={handleInputChange}></textarea>
          </div>
          <div className="mb-3">
            <label htmlFor="price" className="form-label">Precio</label>
            <input type="number" className="form-control" id="price" name="price" value={editedProduct.price} onChange={handleInputChange} />
          </div>
          <div className="mb-3">
            <label htmlFor="img" className="form-label">URL de la imagen</label>
            <input type="text" className="form-control" id="img" name="img" value={editedProduct.img} onChange={handleInputChange} />
          </div>
          <div className="mb-3">
            <label htmlFor="stock" className="form-label">Stock</label>
            <input type="number" className="form-control" id="stock" name="stock" value={editedProduct.stock} onChange={handleInputChange} />
          </div>
          <Button variant="primary" onClick={handleEditSave}>Guardar Cambios</Button>{' '}
          <Button variant="secondary" onClick={handleCancelEdit}>Cancelar</Button>
        </form>
      </div>
    );
  }

  return (
    <div className="card shadow-lg" style={{ width: 1170, margin: "30px auto" }}>
      <h1 className="text-uppercase p-2">
        {title} ${price}
      </h1>
      <picture>
        <img className="shadow p-3 rounded-2" src={img} alt={description} />
      </picture>
      <section className="p-3">
        <p>{description}</p>
        <p className="fst-italic fw-bolder">Stock Disponible {stock}</p>
      </section>
      <footer className="card-footer">
        {quantityAdded ? (
          <Link to={'/cart'} className='btn btn-outline-warning mx-2'>Terminar Compra</Link>
        ) : (
          <ItemCount initial={1} stockDisponible={stock} onAdd={handleOnAdd} />
        )}
        <Link to="/" className="btn btn-primary mx-2 col-12">Seguir comprando</Link>
        <Button onClick={() => deleteProduct(id)}>Eliminar Producto</Button>
        <Button onClick={() => setIsEditing(true)}>Editar</Button>
      </footer>
    </div>
  );
};

export default ItemDetail;
