import React, { useState, useEffect } from 'react';
import axios from '../service/axiosConfig';
import { useParams, useNavigate } from 'react-router-dom';
import Button from 'react-bootstrap/Button';

const PurchaseForm = () => {
  const { supplierId } = useParams();
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState('');
  const [quantity, setQuantity] = useState(0);
  const [price, setPrice] = useState(0);
  const [supplier, setSupplier] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchSupplier();
    fetchProducts();
  }, []);

  const fetchSupplier = async () => {
    try {
      const response = await axios.get(`/suppliers/${supplierId}`);
      setSupplier(response.data);
    } catch (error) {
      console.error('Error fetching supplier:', error);
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await axios.get(`/products?category=${supplier?.category}`);
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (selectedProduct && quantity > 0 && price > 0) {
      try {
        await axios.post('/api/purchases', {
          productId: selectedProduct,
          quantity: parseInt(quantity),
          price: parseFloat(price),
          supplierId: supplier.id,
        });
  
        alert('Compra realizada con éxito');
        navigate('/suppliers');
      } catch (error) {
        console.error('Error realizando la compra:', error);
        alert('Error realizando la compra');
      }
    } else {
      alert('Por favor, completa todos los campos correctamente.');
    }
  };
  
  return (
    <div>
      <h2>Comprar Producto - {supplier?.name}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Producto:</label>
          <select
            value={selectedProduct}
            onChange={(e) => setSelectedProduct(e.target.value)}
          >
            <option value="">Selecciona un producto</option>
            {products.map((product) => (
              <option key={product.id} value={product.id}>
                {product.title}
              </option>
            ))}
          </select>
          <input
            type="text"
            placeholder="Nuevo producto"
            value={selectedProduct}
            onChange={(e) => setSelectedProduct(e.target.value)}
          />
        </div>
        <div>
          <label>Cantidad:</label>
          <input
            type="number"
            value={quantity}
            onChange={(e) => setQuantity(e.target.value)}
          />
        </div>
        <div>
          <label>Precio de Compra:</label>
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
        </div>
        <Button type="submit">Realizar Compra</Button>
      </form>
    </div>
  );
};

export default PurchaseForm;
