import React, { useState, useEffect } from 'react';
import axios from '../service/axiosConfig';
import { useNavigate } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import Alert from 'react-bootstrap/Alert';

const PurchaseForm = ({ supplierId }) => {
  const [supplier, setSupplier] = useState(null);
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [quantity, setQuantity] = useState('');
  const [price, setPrice] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const [total, setTotal] = useState(0);
  useEffect(() => {
    if (supplierId) {
      fetchSupplier(supplierId);
    }
  }, [supplierId]);

  const fetchSupplier = async (id) => {
    try {
      const supplierResponse = await axios.get(`/suppliers/${id}`);
      setSupplier(supplierResponse.data);
      // Fetch products of this supplier's category
      const productsResponse = await axios.get(`/products/by-category?category=${supplierResponse.data.category}`);
      setProducts(productsResponse.data);
    } catch (err) {
      console.error('Error fetching supplier or products:', err);
      setError('No se pudo obtener la información del proveedor o productos');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedProduct) {
      setError('Debe seleccionar un producto');
      return;
    }

    const quantityNum = parseFloat(quantity);
    if (!quantityNum || quantityNum <= 0) {
      setError('La cantidad debe ser mayor a 0');
      return;
    }

    // Calcular total final
    const finalTotal = parseFloat(selectedProduct.price) * quantityNum;

    try {
      const purchaseData = {
        supplierId: Number(supplierId),
        productId: selectedProduct.id,
        quantity: quantityNum,
        price: parseFloat(selectedProduct.price),
        total: parseFloat(finalTotal.toFixed(2)),
        type: 'compra',
        buyer_info: JSON.stringify({
          supplierId: Number(supplierId),
          productId: selectedProduct.id,
          quantity: quantityNum,
          price: parseFloat(selectedProduct.price),
          total: parseFloat(finalTotal.toFixed(2)),
          productName: selectedProduct.title
        })
      };

      const response = await axios.post('/api/purchases', purchaseData);
      if (response.data) {
        navigate('/purchase-report');
      }
    } catch (error) {
      console.error('Error al crear la compra:', error);
      setError('Error al registrar la compra');
    }
  };

  const handleProductChange = (e) => {
    const productId = parseInt(e.target.value);
    const product = products.find(p => p.id === productId);
    setSelectedProduct(product);
    if (product) {
      setPrice(product.price.toString());
 
      if (quantity) {
        const newTotal = parseFloat((Number(quantity) * product.price).toFixed(2));
        setTotal(newTotal);
      }
    } else {
      setPrice('');
      setTotal(0);
    }
    setError('');
  };
  const handleQuantityChange = (e) => {
    const newQuantity = e.target.value;
    setQuantity(newQuantity);

    if (selectedProduct && newQuantity) {
      const calculatedTotal = parseFloat(selectedProduct.price) * parseFloat(newQuantity);
      setTotal(parseFloat(calculatedTotal.toFixed(2)));
    } else {
      setTotal(0);
    }
  };

  const handlePriceChange = (e) => {
    const newPrice = e.target.value;
    setPrice(newPrice);
  
    // Recalcular total si hay cantidad
    if (quantity && newPrice) {
      const newTotal = parseFloat((Number(quantity) * Number(newPrice)).toFixed(2));
      setTotal(newTotal);
    } else {
      setTotal(0);
    }
    setError('');
  };
  
  return (
    <Card className="p-4 mx-auto my-4" style={{ maxWidth: '600px' }}>
      <Card.Body>
        <Card.Title>Nueva Compra</Card.Title>
        {supplier && <Card.Subtitle className="mb-4">Proveedor: {supplier.name}</Card.Subtitle>}
        {error && <Alert variant="danger">{error}</Alert>}

        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label>Producto:</Form.Label>
            <Form.Select
              value={selectedProduct?.id || ''}
              onChange={handleProductChange}
            >
              <option value="">Selecciona un Producto Existente</option>
              {products.map((product) => (
                <option key={product.id} value={product.id}>
                  {product.title}
                </option>
              ))}
            </Form.Select>
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Cantidad:</Form.Label>
            <Form.Control
              type="number"
              min="1"
              value={quantity}
              onChange={handleQuantityChange}
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Precio:</Form.Label>
            <Form.Control
              type="number"
              min="0"
              value={price}
              onChange={handlePriceChange}
              disabled={!!selectedProduct}
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Total:</Form.Label>
            <Form.Control
              type="number"
              value={total}
              disabled
            />
          </Form.Group>

          <div className="d-flex justify-content-between">
            <Button variant="primary" type="submit"disabled={!selectedProduct || !quantity || total <= 0}>
              Guardar Compra
            </Button>
            <Button
              variant="secondary"
              onClick={() => navigate(`/form/product/new?supplierId=${supplierId}`)}
            >
              Agregar Nuevo Producto
            </Button>
          </div>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default PurchaseForm;
