import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext'; // Asegúrate de que la ruta sea correcta
import { Table, Button, Spinner } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const ClientesList = () => {
  const [clientes, setClientes] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user, auth } = useAuth(); // Obtener auth también del contexto

  useEffect(() => {
    const fetchClientes = async () => {
      try {
        if (auth.currentUser) { // Usar auth.currentUser en lugar de user
          const token = await auth.currentUser.getIdToken();
          const response = await axios.get('http://localhost:3001/api/clients', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setClientes(response.data);
        } else {
          console.error("Usuario no autenticado o user es null.");
        }
      } catch (error) {
        console.error('Error fetching clients:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchClientes();
  }, [auth.currentUser]); // Actualizar la dependencia

  const handleDeleteCliente = async (id) => {
    if (window.confirm('¿Estás seguro de que deseas eliminar este cliente?')) {
      setLoading(true);
      try {
        if (auth.currentUser) { // Usar auth.currentUser aquí también
          const token = await auth.currentUser.getIdToken();
          await axios.delete(`http://localhost:3001/api/clients/${id}`, {
            headers: { Authorization: `Bearer ${token}` }
          });
          // Actualizar la lista después de eliminar
          setClientes(clientes.filter(cliente => cliente.id !== id));
        }
      } catch (error) {
        console.error('Error deleting client:', error);
      } finally {
        setLoading(false);
      }
    }
  };

  if (loading) {
    return <Spinner animation="border" />;
  }

  return (
    <div className="container mt-4">
      <h2>Lista de Clientes</h2>
      <Link to="/clients/new" className="btn btn-primary mb-3">Nuevo Cliente</Link>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Email</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {clientes.map(cliente => (
            <tr key={cliente.id}>
              <td>{cliente.id}</td>
              <td>{cliente.name}</td>
              <td>{cliente.email}</td>
              <td>
                <Link to={`/clients/edit/${cliente.id}`} className="btn btn-warning me-2">
                  Editar
                </Link>
                <Button variant="danger" onClick={() => handleDeleteCliente(cliente.id)}>
                  Eliminar
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default ClientesList;
