import React, { useState, useEffect } from "react";
import ApexCharts from "apexcharts";
import { Table, Form, Button, Row, FormSelect } from "react-bootstrap";
import axios from "../../service/axiosConfig";

const PurchaseReport = () => {
  const [filteredData, setFilteredData] = useState([]);
  const [filters, setFilters] = useState({
    orderNumber: "",
    productName: "",
    category: "",
    supplier: "",
    startDate: "",
    endDate: ""
  });

  useEffect(() => {
    fetchPurchases();
  }, []);

  useEffect(() => {
    if (filteredData.length > 0) {
      // Configuración de datos para el gráfico de barras
      const chartData = filteredData.flatMap((item) =>
        item.OrderItems ? item.OrderItems.map(orderItem => ({
          x: orderItem.product_name || "Sin nombre",
          y: orderItem.quantity || 0
        })) : []
      );

      const options = {
        chart: {
          type: "bar",
          height: 350
        },
        series: [{
          name: "Cantidad Comprada",
          data: chartData.map((data) => data.y)
        }],
        xaxis: {
          categories: chartData.map((data) => data.x)
        }
      };

      const chart = new ApexCharts(document.querySelector("#chart"), options);
      chart.render();

      return () => chart.destroy();
    }
  }, [filteredData]);

  const fetchPurchases = async () => {
    try {
      const response = await axios.get("/api/orders?type=compra");
      setFilteredData(response.data);
    } catch (error) {
      console.error("Error obteniendo las compras:", error);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({
      ...filters,
      [name]: value
    });
  };

  const handleClearFilters = () => {
    setFilters({ orderNumber: "", productName: "", category: "", supplier: "", startDate: "", endDate: "" });
    fetchPurchases();
  };

  const applyFilters = () => {
    const filtered = filteredData.filter(item => {
      const itemDate = new Date(item.date);
      const startDate = filters.startDate ? new Date(filters.startDate) : null;
      const endDate = filters.endDate ? new Date(filters.endDate) : null;

      return (
        (!filters.orderNumber || item.id.toString().includes(filters.orderNumber)) &&
        (!filters.productName || item.OrderItems?.some(orderItem => orderItem.product_name.toLowerCase().includes(filters.productName.toLowerCase()))) &&
        (!filters.category || item.OrderItems?.some(orderItem => orderItem.category?.toLowerCase().includes(filters.category.toLowerCase()))) &&
        (!filters.supplier || item.buyer_info?.supplier?.toLowerCase().includes(filters.supplier.toLowerCase())) &&
        (!startDate || itemDate >= startDate) &&
        (!endDate || itemDate <= endDate)
      );
    });
    setFilteredData(filtered);
  };

  return (
    <div className="justify-content-center container-fluid">
      <h1>Reporte de Compras</h1>
      <Form className="container-fluid justify-content-center my-4">
        <Row className="justify-content-center">
          <Form.Group className="col-1" controlId="orderNumber">
            <Form.Label>Orden</Form.Label>
            <Form.Control
              type="text"
              name="orderNumber"
              value={filters.orderNumber}
              onChange={handleFilterChange}
            />
          </Form.Group>
          <Form.Group className="col-2" controlId="productName">
            <Form.Label>Nombre del producto</Form.Label>
            <Form.Control
              type="text"
              name="productName"
              value={filters.productName}
              onChange={handleFilterChange}
            />
          </Form.Group>
          <Form.Group className="col-3" controlId="category">
            <Form.Label>Categoría</Form.Label>
            <FormSelect name="category" value={filters.category} onChange={handleFilterChange}>
              <option value="">Seleccionar categoría</option>
              <option value="notebook">Notebook</option>
              <option value="celular">Celular</option>
              <option value="tablet">Tablet</option>
            </FormSelect>
          </Form.Group>
          <Form.Group className="col-2" controlId="supplier">
            <Form.Label>Proveedor</Form.Label>
            <Form.Control
              type="text"
              name="supplier"
              value={filters.supplier}
              onChange={handleFilterChange}
            />
          </Form.Group>
          <Form.Group className="col-2" controlId="startDate">
            <Form.Label>Fecha Inicio</Form.Label>
            <Form.Control
              type="date"
              name="startDate"
              value={filters.startDate}
              onChange={handleFilterChange}
            />
          </Form.Group>
          <Form.Group className="col-2" controlId="endDate">
            <Form.Label>Fecha Fin</Form.Label>
            <Form.Control
              type="date"
              name="endDate"
              value={filters.endDate}
              onChange={handleFilterChange}
            />
          </Form.Group>
        </Row>
        <Button className="mt-4" onClick={applyFilters}>Filtrar</Button>
        <Button className="mt-4 mx-3 btn btn-secondary" onClick={handleClearFilters}>Borrar Filtros</Button>
      </Form>
      <Table className="table-responsive table w-75 p-5 m-5" striped bordered hover>
        <thead>
          <tr>
            <th>Orden</th>
            <th>Cantidad</th>
            <th>Producto</th>
            <th>Categoría</th>
            <th>Proveedor</th>
            <th>Fecha</th>
          </tr>
        </thead>
        <tbody>
          {filteredData.flatMap((item) =>
            item.OrderItems?.map(orderItem => (
              <tr key={orderItem.id}>
                <td>{item.id}</td>
                <td>{orderItem.quantity || "N/A"}</td>
                <td>{orderItem.product_name || "N/A"}</td>
                <td>{orderItem.category || "N/A"}</td>
                <td>{item.buyer_info?.supplier || "N/A"}</td>
                <td>{item.date || "N/A"}</td>
              </tr>
            )) || []
          )}
        </tbody>
      </Table>
      <div id="chart"></div>
    </div>
  );
};

export default PurchaseReport;
