import "../App.css";
import { useState, useContext, useEffect } from "react";
import { CartContext } from "../context/cartContext";
import { collection, addDoc } from 'firebase/firestore';
import { db } from '../components/service/firebaseConfig'; 
import { useAuth } from "../context/AuthContext";
import axios from "axios";

const CheckOut = () => {
  const [loading, setLoading] = useState(false);
  const [orderId, setOrderId] = useState("");
  const { cart, total, clearCart } = useContext(CartContext);
  const { user } = useAuth();

  const createOrder = async () => {
    if (!user) {
      alert("Debe iniciar sesión para completar la compra.");
      return;
    }
    
    setLoading(true);
    console.log("Inicio de creación de orden con usuario:", user);  // Verificar usuario autenticado
    
    try {
      // Construir el objeto de orden
      const objOrder = {
        buyer: {
          uid: user.uid,
          email: user.email,
        },
        items: cart,
        total: total,
        date: new Date().toISOString(),
      };
  
      // Guardar la orden en Firestore
      console.log("Intentando guardar la orden en Firestore...");
      const ordersCollectionRef = collection(db, 'ordenes');
      const docRef = await addDoc(ordersCollectionRef, objOrder);
      setOrderId(docRef.id);
      console.log("Orden creada con éxito con ID:", docRef.id);
  
      // Actualizar el stock de cada producto en Datastore
      for (let item of cart) {
        console.log(`Reduciendo stock para producto ID: ${item.id} con cantidad: ${item.quantity}`);
        await axios.post(`http://localhost:3001/api/products/${item.id}/decrementStock`, {
          quantity: item.quantity
        });
      }
  
      // Limpiar el carrito después de procesar la compra
      clearCart();
    } catch (error) {
      console.error("Error al crear la orden o actualizar el stock:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (cart.length === 0) {
      alert("El carrito está vacío. Agrega productos antes de proceder.");
    }
  }, [cart]);

  if (loading) {
    return <h1>Se está generando su orden...</h1>;
  }

  if (orderId) {
    return <h1>El ID de su orden es: {orderId}</h1>;
  }

  return (
    <div>
      <h3>Resumen de tu compra</h3>
      {cart.map(({ id, img, title, price, quantity }) => (
        <div key={id}>
          <p className="text-uppercase fw-bolder" id="itemName">
            {title}
          </p>
          <p>Precio Unitario: ${price}</p>
          <p>
            <img width={"80px"} src={img} alt="" />
          </p>
          <p>Cantidad: {quantity}</p>
        </div>
      ))}
      <h4>Total a pagar: ${total}</h4>
      <button onClick={createOrder} className="btn btn-primary">
        Confirmar Compra
      </button>
    </div>
  );
};

export default CheckOut;
