import React, { useState, useEffect } from 'react';
import axios from '../service/axiosConfig';
import { Table, Button, Spinner } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const ClientesList = () => {
  const [clientes, setClientes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchClientes = async () => {
      try {
        setLoading(true);
        const response = await axios.get('/api/clients'); // Cambié para que sea similar a ProductList y SupplierList
        console.log('Clientes obtenidos:', response.data);
        setClientes(response.data);
      } catch (error) {
        console.error('Error fetching clients:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchClientes();
  }, []);

  const handleDeleteCliente = async (id) => {
    if (window.confirm('¿Estás seguro de que deseas eliminar este cliente?')) {
      setLoading(true);
      try {
        await axios.delete(`/api/clients/${id}`);
        // Actualizar la lista después de eliminar
        setClientes((prevClientes) => prevClientes.filter((cliente) => cliente.id !== id));
      } catch (error) {
        console.error('Error deleting client:', error);
      } finally {
        setLoading(false);
      }
    }
  };

  if (loading) {
    return (
      <div className="text-center mt-5">
        <Spinner animation="border" />
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <h2>Lista de Clientes</h2>
      <Link to="/form/client" className="btn btn-primary mb-3">Nuevo Cliente</Link>
      {clientes.length === 0 ? (
        <p>No hay clientes disponibles.</p>
      ) : (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>CUIT</th>
              <th>Email</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {clientes.map((cliente) => (
              <tr key={cliente.id}>
                <td>{cliente.id}</td>
                <td>{cliente.name}</td>
                <td>{cliente.cuit}</td>
                <td>{cliente.email}</td>
                <td>
                  <Link to={`/form/client/${cliente.id}`} className="btn btn-warning me-2">
                    Editar
                  </Link>
                  <Button variant="danger" onClick={() => handleDeleteCliente(cliente.id)}>
                    Eliminar
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </div>
  );
};

export default ClientesList;
