import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import FormGroup from 'react-bootstrap/esm/FormGroup';
import { getProductById } from '../../asyncMock';

const EditItemForm = ({ onSave }) => {
    const { itemId } = useParams();
    const [product, setProduct] = useState(null);
    const navigate = useNavigate();
  
    useEffect(() => {
      getProductById(itemId).then(setProduct);
    }, [itemId]);
  
    const handleSubmit = (e) => {
      e.preventDefault();
      onSave(product);
      navigate('/');
    };
  
    const handleChange = (e) => {
      const { name, value } = e.target;
      setProduct((prevProduct) => ({
        ...prevProduct,
        [name]: value,
      }));
    };
  
    if (!product) {
      return <div>Loading...</div>;
    }

    return (
        <Form className='container' onSubmit={handleSubmit}>
            <Row className='justify-content-center col-12'>
                <FormGroup className='col-6'>
                    <Form.Label>Nombre</Form.Label>
                    <Form.Control type="text" value={title} onChange={(e) => product(e.target.value)} />
                </FormGroup>
               
            </Row>
            <Row className='justify-content-center'>
                <FormGroup className='col-8'>
                    <Form.Label>Descripci√≥n</Form.Label>
                    <Form.Control type='text' value={description} onChange={(e) => product(e.target.value)} />
                </FormGroup>
            </Row>
            <Row className='justify-content-center my-3'>
                <FormGroup className='col-4'>
                    <Form.Label>Stock</Form.Label>
                    <Form.Control type="text" value={stock} onChange={(e) => setStock(e.target.value)} />
                </FormGroup>
                <FormGroup className='col-4'>
                    <Form.Label>Precio</Form.Label>
                    <Form.Control type="text" value={price} onChange={(e) => setPrice(e.target.value)} />
                </FormGroup>
            </Row>
            <Button type="submit">Guardar Cambios</Button>
        </Form>
    );
};

export default EditItemForm;
