import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import FormGroup from 'react-bootstrap/FormGroup';
import axios from 'axios';

const ProductForm = () => {
    const { itemId } = useParams(); 
    const navigate = useNavigate();
    const isEditMode = Boolean(itemId); 

    const [product, setProduct] = useState({
        title: '',
        description: '',
        stock: 0,
        price: 0,
        img: '',
        purchase_price: 0,
        category: '',
        supplier_id: '' 
    });

    useEffect(() => {
        if (isEditMode) {
            axios.get(`http://localhost:3001/api/products/${itemId}`)
                .then((response) => setProduct(response.data))
                .catch((error) => console.error("Error fetching product:", error));
        }
    }, [itemId, isEditMode]);

    const handleSubmit = (e) => {
        e.preventDefault();
        const url = isEditMode ? `http://localhost:3001/api/productos/${itemId}` : 'http://localhost:3001/api/productos';
        const request = isEditMode ? axios.put(url, product) : axios.post(url, product);
        request
            .then(() => {
                alert(isEditMode ? "Producto actualizado con éxito" : "Producto agregado con éxito");
                navigate('/products');
            })
            .catch((error) => console.error(isEditMode ? "Error updating product:" : "Error adding product:", error));
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setProduct((prevProduct) => ({
            ...prevProduct,
            [name]: name === 'price' || name === 'stock' || name === 'purchase_price' ? Number(value) : value,
        }));
    };

    if (isEditMode && !product) {
        return <div>Loading...</div>;
    }

    return (
        <Form className='container' onSubmit={handleSubmit}>
            <Row className='justify-content-center col-12'>
                <FormGroup className='col-6'>
                    <Form.Label htmlFor="title">Nombre</Form.Label>
                    <Form.Control 
                        type="text" 
                        id="title" 
                        name="title" 
                        value={product.title} 
                        onChange={handleChange} 
                    />
                </FormGroup>
            </Row>
            <Row className='justify-content-center'>
                <FormGroup className='col-8'>
                    <Form.Label htmlFor="description">Descripción</Form.Label>
                    <Form.Control 
                        type='text' 
                        id="description" 
                        name="description" 
                        value={product.description} 
                        onChange={handleChange} 
                    />
                </FormGroup>
            </Row>
            <Row className='justify-content-center my-3'>
                <FormGroup className='col-4'>
                    <Form.Label htmlFor="stock">Stock</Form.Label>
                    <Form.Control 
                        type="number" 
                        id="stock" 
                        name="stock" 
                        value={product.stock} 
                        onChange={handleChange} 
                    />
                </FormGroup>
                <FormGroup className='col-4'>
                    <Form.Label htmlFor="price">Precio</Form.Label>
                    <Form.Control 
                        type="number" 
                        id="price" 
                        name="price" 
                        value={product.price} 
                        onChange={handleChange} 
                    />
                </FormGroup>
            </Row>
            <Row className='justify-content-center'>
                <FormGroup className='col-8'>
                    <Form.Label htmlFor="img">Imagen (URL)</Form.Label>
                    <Form.Control 
                        type='text' 
                        id="img" 
                        name="img" 
                        value={product.img} 
                        onChange={handleChange} 
                    />
                </FormGroup>
            </Row>
            <Row className='justify-content-center'>
                <FormGroup className='col-4'>
                    <Form.Label htmlFor="purchase_price">Precio de Compra</Form.Label>
                    <Form.Control 
                        type="number" 
                        id="purchase_price" 
                        name="purchase_price" 
                        value={product.purchase_price} 
                        onChange={handleChange} 
                    />
                </FormGroup>
                <FormGroup className='col-4'>
                    <Form.Label htmlFor="category">Categoría</Form.Label>
                    <Form.Control 
                        type="text" 
                        id="category" 
                        name="category" 
                        value={product.category} 
                        onChange={handleChange} 
                    />
                </FormGroup>
            </Row>
            <Row className='justify-content-center'>
                <FormGroup className='col-8'>
                    <Form.Label htmlFor="supplier_id">ID del Proveedor</Form.Label>
                    <Form.Control 
                        type="text" 
                        id="supplier_id" 
                        name="supplier_id" 
                        value={product.supplier_id} 
                        onChange={handleChange} 
                    />
                </FormGroup>
            </Row>
            <Button type="submit">
                {isEditMode ? "Guardar Cambios" : "Agregar Producto"}
            </Button>
        </Form>
    );
};

export default ProductForm;
