import React, { createContext, useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { onAuthStateChanged, signInWithPopup, signOut } from 'firebase/auth';
import { auth, googleProvider } from '../components/service/firebaseConfig';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

const adminEmails = ["guillermo.ibanezc@gmail.com", "trek0.88@gmail.com"];

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Configurar interceptor global de Axios
  useEffect(() => {
    const interceptor = axios.interceptors.request.use(
      async (config) => {
        if (auth.currentUser) {
          const token = await auth.currentUser.getIdToken();
          config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
      },
      (error) => {
        return Promise.reject(error);
      }
    );

    return () => axios.interceptors.request.eject(interceptor);
  }, []);

  const fetchUserRoleAndMySQLId = async (email) => {
    try {
      const response = await axios.get(`http://localhost:3001/api/clients/email/${email}`);
      const userData = response.data;

      if (adminEmails.includes(email)) {
        userData.role = 'admin';
      } else {
        userData.role = 'user';
      }

      setUser(userData);
    } catch (error) {
      if (error.response && error.response.status === 404) {
        // Si el usuario no existe en la base de datos, crearlo
        const newUser = {
          email,
          role: adminEmails.includes(email) ? 'admin' : 'user',
        };

        const response = await axios.post('http://localhost:3001/api/clients', newUser);
        setUser(response.data);
      } else {
        console.error('Error al obtener el rol del usuario o registrar en MySQL:', error);
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (firebaseUser) => {
      if (firebaseUser) {
        fetchUserRoleAndMySQLId(firebaseUser.email);
      } else {
        setUser(null);
        setLoading(false);
      }
    });

    return () => unsubscribe();
  }, []);

  const loginWithGoogle = async () => {
    try {
      await signInWithPopup(auth, googleProvider);
      navigate('/');
    } catch (error) {
      console.error('Error al iniciar sesi贸n:', error);
    }
  };

  const logout = async () => {
    try {
      await signOut(auth);
      setUser(null);
      navigate('/');
    } catch (error) {
      console.error('Error al cerrar sesi贸n:', error);
    }
  };

  const value = {
    user,
    loading,
    loginWithGoogle,
    logout,
    isAdmin: user?.isAdmin || false,
  };

  return (
    <AuthContext.Provider value={{ user, loading, loginWithGoogle, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
