import { signInWithPopup, signOut } from "firebase/auth";
import { auth, googleProvider } from "../service/firebaseConfig";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from 'axios';

export const Auth = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("firebaseToken");
    if (token) {
      navigate("/protected"); 
    }
  }, [navigate]);

  const signInWithGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const idToken = await result.user.getIdToken();

      // Enviar el idToken al backend
      const response = await axios.post('http://localhost:3001/auth/login', {
        firebaseToken: idToken,
      });

      if (response.data.ok) {
        localStorage.setItem("firebaseToken", idToken);  // Almacenar el token si la autenticación fue exitosa
        navigate("/protected");
      } else {
        console.error("Error en la autenticación:", response);
        logout();
      }
    } catch (error) {
      console.error("Error en el inicio de sesión con Google:", error);
    }
  };

  const logout = async () => {
    try {
      await signOut(auth);
      localStorage.removeItem("firebaseToken");
      navigate("/");
    } catch (error) {
      console.error("Error al cerrar sesión:", error);
    }
  };

  return (
    <div>
      <h1>Login</h1>
      <button onClick={signInWithGoogle} className="btn btn-outline-primary mx-2">Iniciar sesión con Google</button>
      <button onClick={logout} className="btn btn-outline-terciary mx-2">Cerrar sesión</button>
    </div>
  );
};

export default Auth;
