// ItemListContainer.js
import React, { useEffect, useState } from 'react';
import ItemList from "../ItemList/ItemList";
import { useParams } from 'react-router-dom';
import { getDocs, collection, query, where } from 'firebase/firestore';
import { db } from '../service/firebaseConfig';

const ItemListContainer = ({ greeting }) => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const { categoryId } = useParams();

    useEffect(() => {
        setLoading(true);

        // Configuración de la referencia de consulta según si hay un `categoryId` o no
        const collectionRef = categoryId
            ? query(collection(db, 'items'), where('category', '==', categoryId))
            : collection(db, 'items');

        // Obtención de datos desde Firebase
        getDocs(collectionRef)
            .then((response) => {
                const productsAdapted = response.docs.map((doc) => {
                    const data = doc.data();
                    return { id: doc.id, ...data };
                });
                setProducts(productsAdapted);
            })
            .catch((error) => {
                console.error("Error fetching products:", error);
                alert("Error al obtener los productos. Verifica la configuración y las reglas de Firestore.");
            })
            .finally(() => {
                setLoading(false);
            });
    }, [categoryId]);

    return (
        <div className="fw-bolder">
            <h1 className="rounded-5 text-uppercase bd-blue-600">{greeting}</h1>
            <div className="text-dark-emphasis fw-bolder">
                {loading ? (
                    <div>...loading</div>
                ) : (
                    <ItemList products={products} />
                )}
            </div>
        </div>
    );
};

export default ItemListContainer;



