import React, { useState, useEffect } from 'react';
import axios from '../service/axiosConfig';

const PurchaseForm = ({ supplierId, onSave }) => {
  const [products, setProducts] = useState([]);
  const [productId, setProductId] = useState(null);
  const [quantity, setQuantity] = useState(0);
  const [purchasePrice, setPurchasePrice] = useState(0);
  const [newProductName, setNewProductName] = useState("");
  const [category, setCategory] = useState("");
  
  useEffect(() => {
    fetchProductsBySupplier();
  }, []);

  const fetchProductsBySupplier = async () => {
    try {
      const response = await axios.get(`/api/products?supplierId=${supplierId}`);
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleSave = async () => {
    const purchaseData = {
      supplierId,
      productId,
      quantity,
      purchasePrice,
      newProductName,
      category,
    };
    // Enviar datos al backend para registrar la compra y actualizar el stock.
    onSave(purchaseData);
  };

  return (
    <div>
      <h3>Registrar Compra</h3>
      <select onChange={(e) => setProductId(e.target.value)}>
        <option value="">Seleccionar Producto Existente</option>
        {products.map((product) => (
          <option key={product.id} value={product.id}>
            {product.title}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Nombre del Producto Nuevo"
        value={newProductName}
        onChange={(e) => setNewProductName(e.target.value)}
      />
      <input
        type="number"
        placeholder="Cantidad"
        value={quantity}
        onChange={(e) => setQuantity(Number(e.target.value))}
      />
      <input
        type="number"
        placeholder="Precio de Compra"
        value={purchasePrice}
        onChange={(e) => setPurchasePrice(Number(e.target.value))}
      />
      <button onClick={handleSave}>Guardar Compra</button>
    </div>
  );
};

export default PurchaseForm;
