import React, { useState, useEffect } from 'react';
import axios from '../service/axiosConfig';
import { Form, Button, Container, Row, Col } from 'react-bootstrap';

const PurchaseForm = ({ supplierId, onSave }) => {
  const [supplier, setSupplier] = useState(null);
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [quantity, setQuantity] = useState(0);
  const [price, setPrice] = useState(0);

  useEffect(() => {
    const fetchSupplier = async () => {
      try {
        const supplierResponse = await axios.get(`/suppliers/${supplierId}`);
        setSupplier(supplierResponse.data);
        const productsResponse = await axios.get(`/api/products?category=${supplierResponse.data.category}`);
        setProducts(productsResponse.data);
      } catch (error) {
        console.error('Error fetching supplier or products:', error);
      }
    };
    fetchSupplier();
  }, [supplierId]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({
      supplierId,
      productId: selectedProduct ? selectedProduct.id : null,
      quantity,
      price,
      newProductName: !selectedProduct ? prompt('Ingrese el nombre del nuevo producto:') : null,
      category: supplier?.category || ''
    });
  };

  return (
    <Container>
      <h2 className="my-4">Compra de Productos - Proveedor: {supplier?.name}</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group as={Row} className="mb-3" controlId="formProduct">
          <Form.Label column sm="2">Producto:</Form.Label>
          <Col sm="10">
            <Form.Control
              as="select"
              value={selectedProduct?.id || ''}
              onChange={(e) => {
                if (e.target.value === 'new') {
                  setSelectedProduct(null);
                  setPrice(0);
                } else {
                  const product = products.find(p => p.id.toString() === e.target.value);
                  setSelectedProduct(product);
                  setPrice(product?.price || 0);
                }
              }}
            >
              <option value="">Selecciona un Producto</option>
              {products.map((product) => (
                <option key={product.id} value={product.id}>{product.title}</option>
              ))}
              <option value="new">Agregar Nuevo Producto</option>
            </Form.Control>
          </Col>
        </Form.Group>
        <Form.Group as={Row} className="mb-3" controlId="formQuantity">
          <Form.Label column sm="2">Cantidad:</Form.Label>
          <Col sm="10">
            <Form.Control
              type="number"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
              required
            />
          </Col>
        </Form.Group>
        <Form.Group as={Row} className="mb-3" controlId="formPrice">
          <Form.Label column sm="2">Precio:</Form.Label>
          <Col sm="10">
            <Form.Control
              type="number"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              required
            />
          </Col>
        </Form.Group>
        <Button variant="primary" type="submit">Guardar Compra</Button>
      </Form>
    </Container>
  );
};

export default PurchaseForm;
