import React from 'react';
import { Navbar, Container, Nav, NavDropdown, Offcanvas } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import PropTypes from 'prop-types';
import CartWidget from './CartWidget';

const AdminNav = () => (
  <>
    <NavLink to="/admin/clientes" className="nav-link">Clientes</NavLink>
    <NavLink to="/admin/suppliers" className="nav-link">Proveedores</NavLink>
    <NavLink to="/admin/products" className="nav-link">Productos</NavLink>
    <NavDropdown title="Reportes" id="reportDropdown">
      <NavLink to="/admin/sales-report" className="dropdown-item">Ventas</NavLink>
      <NavLink to="/admin/purchase-report" className="dropdown-item">Compras</NavLink>
    </NavDropdown>
  </>
);

const UserNav = () => (
  <NavLink to="/cart" className="nav-link">
    <CartWidget />
  </NavLink>
);

const NavBar = () => {
  const { user, loginWithGoogle, logout } = useAuth();
  const expand = 'lg';

  const handleLogout = async () => {
    try {
      await logout();
    } catch (error) {
      console.error('Error al cerrar sesión:', error);
    }
  };

  return (
    <Navbar bg="light" expand={expand} className="mb-3">
      <Container fluid>
        <NavLink to="/" className="navbar-brand">
          Mi Tienda
        </NavLink>
        
        <Navbar.Toggle aria-controls={`offcanvasNavbar-expand-${expand}`} />
        
        <Navbar.Offcanvas
          id={`offcanvasNavbar-expand-${expand}`}
          aria-labelledby={`offcanvasNavbarLabel-expand-${expand}`}
          placement="end"
        >
          <Offcanvas.Header closeButton>
            <Offcanvas.Title id={`offcanvasNavbarLabel-expand-${expand}`}>
              Menú
            </Offcanvas.Title>
          </Offcanvas.Header>
          
          <Offcanvas.Body>
            <Nav className="justify-content-end flex-grow-1 pe-3">
              <NavLink to="/" className="nav-link">Inicio</NavLink>
              <NavLink to="/products" className="nav-link">Productos</NavLink>
              
              {user && (
                user.role === 'admin' ? <AdminNav /> : <UserNav />
              )}

              <NavLink to="/contact" className="nav-link">Contacto</NavLink>

              {user ? (
                <button 
                  onClick={handleLogout} 
                  className="btn btn-outline-primary mx-2"
                >
                  Cerrar sesión
                </button>
              ) : (
                <button 
                  onClick={loginWithGoogle} 
                  className="btn btn-primary"
                >
                  Iniciar sesión con Google
                </button>
              )}
            </Nav>
          </Offcanvas.Body>
        </Navbar.Offcanvas>
      </Container>
    </Navbar>
  );
};

NavBar.propTypes = {
  user: PropTypes.shape({
    role: PropTypes.string,
    email: PropTypes.string
  })
};

export default NavBar;
