import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Button from 'react-bootstrap/Button';
import { useAuth } from '../../context/AuthContext'; 
import { useNavigate } from 'react-router-dom';

const ClienteList = () => {
  const [clients, setClients] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchClients = async () => {
      setLoading(true);
      try {
        if (user) {
          const token = await user.getIdToken();
          const response = await axios.get('http://localhost:3001/api/clients', {
            headers: { Authorization: `Bearer ${token}` }
          });
          setClients(response.data);
        } else {
          console.error("Usuario no autenticado");
        }
      } catch (error) {
        console.error('Error fetching clients:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchClients();
  }, [user]);

  const handleDeleteClient = async (id) => {
    if (window.confirm('¿Estás seguro de que deseas eliminar este cliente?')) {
      setLoading(true);
      try {
        if (user) {
          const token = await user.getIdToken();
          await axios.delete(`http://localhost:3001/api/clients/${id}`, {
            headers: { Authorization: `Bearer ${token}` }
          });
          setClients((prevClients) =>
            prevClients.filter((client) => client.id !== id)
          );
        }
      } catch (error) {
        console.error('Error deleting client:', error);
      } finally {
        setLoading(false);
      }
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Lista de Clientes</h1>
      <Button className="btn btn-primary mb-3" onClick={() => navigate('/form/client')}>Agregar Cliente</Button>
      {clients.map((client) => (
        <div key={client.id}>
          <p>Nombre: {client.name}</p>
          <p>CUIT: {client.cuit}</p>
          <p>Email: {client.email}</p>
          <Button className=' btn-warning mx-2' onClick={() => navigate(`/form/client/${client.id}`)}>Editar</Button>
          <Button className=' btn-danger'  onClick={() => handleDeleteClient(client.id)}>Eliminar</Button>
          <hr />
        </div>
      ))}
    </div>
  );
};

export default ClienteList;
