import React, { useState, useEffect } from 'react';
import axios from '../service/axiosConfig';
import { useNavigate } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import Alert from 'react-bootstrap/Alert';

const PurchaseForm = ({ supplierId, onSave }) => {
  const [supplier, setSupplier] = useState(null);
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [quantity, setQuantity] = useState(0);
  const [price, setPrice] = useState(0);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    if (supplierId) {
      fetchSupplier(supplierId);
    }
  }, [supplierId]);

  const fetchSupplier = async (id) => {
    try {
      const supplierResponse = await axios.get(`/suppliers/${id}`);
      setSupplier(supplierResponse.data);
      // Fetch products of this supplier's category
      const productsResponse = await axios.get(`/products/by-category?category=${supplierResponse.data.category}`);
      setProducts(productsResponse.data);
    } catch (err) {
      console.error('Error fetching supplier or products:', err);
      setError('No se pudo obtener la información del proveedor o productos');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedProduct && !price) {
      setError('Debe seleccionar un producto existente o agregar un nuevo producto con su precio.');
      return;
    }

    if (quantity <= 0) {
      setError('La cantidad debe ser mayor a cero.');
      return;
    }

    const total = price * quantity;

    if (typeof onSave === 'function') {
      onSave({
        supplierId,
        productId: selectedProduct?.id || null,
        quantity,
        price,
        total,
      });
    } else {
      try {
        const response = await axios.post('/api/purchases', {
          supplierId,
          productId: selectedProduct?.id || null,
          quantity,
          price,
          total,
        });
        if (response.status === 200) {
          alert('Compra registrada con éxito');
          navigate('/'); // Redirige a la página principal o a la vista deseada
        }
      } catch (error) {
        console.error('Error al registrar la compra:', error);
        alert('Error al registrar la compra. Por favor, inténtelo de nuevo.');
      }
    }
  };

  return (
    <Card className="p-4 mx-auto my-4" style={{ maxWidth: '600px' }}>
      <Card.Body>
        <Card.Title>Nueva Compra</Card.Title>
        {supplier && <Card.Subtitle className="mb-4">Proveedor: {supplier.name}</Card.Subtitle>}
        {error && <Alert variant="danger">{error}</Alert>}

        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label>Producto:</Form.Label>
            <Form.Select
              value={selectedProduct?.id || ''}
              onChange={(e) => {
                const product = products.find((p) => p.id === parseInt(e.target.value));
                setSelectedProduct(product);
                setPrice(product ? product.price : 0);
              }}
            >
              <option value="">Selecciona un Producto Existente</option>
              {products.map((product) => (
                <option key={product.id} value={product.id}>
                  {product.title}
                </option>
              ))}
            </Form.Select>
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Cantidad:</Form.Label>
            <Form.Control
              type="number"
              min="1"
              value={quantity}
              onChange={(e) => setQuantity(parseInt(e.target.value))}
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Precio:</Form.Label>
            <Form.Control
              type="number"
              min="0"
              value={price}
              onChange={(e) => setPrice(parseFloat(e.target.value))}
              disabled={!!selectedProduct}
            />
          </Form.Group>

          <div className="d-flex justify-content-between">
            <Button variant="primary" type="submit">
              Guardar Compra
            </Button>
            <Button
              variant="secondary"
              onClick={() => navigate(`/form/product/new?supplierId=${supplierId}`)}
            >
              Agregar Nuevo Producto
            </Button>
          </div>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default PurchaseForm;
