import React, { useState, useEffect, useContext } from 'react';
import { CartContext } from '../context/cartContext';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';

const CheckOut = () => {
  const [loading, setLoading] = useState(false);
  const [orderId, setOrderId] = useState(null);
  const [error, setError] = useState(null);
  const { cart, total, clearCart } = useContext(CartContext);
  const { user, loading: authLoading } = useAuth();

  const createOrder = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      if (!user || typeof user.getIdToken !== 'function') {
        throw new Error('Usuario no autenticado o inválido');
      }

      const token = await user.getIdToken();

      const objOrder = {
        items: cart.map(item => ({
          id: item.id,
          title: item.title,
          price: item.price,
          quantity: item.quantity,
        })),
        total: total,
        date: new Date().toISOString(),
        status: 'created',
        userId: user.uid,
      };

      const response = await axios.post(
        "http://localhost:3001/api/orders",
        objOrder,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      if (response.data && response.data.id) {
        setOrderId(response.data.id);
        clearCart();
      } else {
        throw new Error('Respuesta inválida del servidor');
      }
    } catch (error) {
      console.error("Error al crear la orden:", error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (cart.length === 0 && !orderId) {
      setError("El carrito está vacío. Agrega productos antes de proceder.");
    }
  }, [cart, orderId]);

  if (authLoading || loading) {
    return (
      <div className="container mt-4">
        <div className="alert alert-info">
          Se está generando su orden...
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mt-4">
        <div className="alert alert-danger">
          {error}
        </div>
      </div>
    );
  }

  if (orderId) {
    return (
      <div className="container mt-4">
        <div className="alert alert-success">
          ¡Orden generada exitosamente! ID: {orderId}
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <h3>Resumen de tu compra</h3>
      {cart.map(({ id, img, title, price, quantity }) => (
        <div key={id} className="card mb-3">
          <div className="row g-0">
            <div className="col-md-2">
              <img src={img} className="img-fluid rounded-start" alt={title} />
            </div>
            <div className="col-md-10">
              <div className="card-body">
                <h5 className="card-title">{title}</h5>
                <p className="card-text">
                  Precio: ${price} x {quantity} = ${price * quantity}
                </p>
              </div>
            </div>
          </div>
        </div>
      ))}
      
      <div className="card mb-3">
        <div className="card-body">
          <h5>Total: ${total}</h5>
          <button 
            className="btn btn-primary"
            onClick={createOrder}
            disabled={cart.length === 0 || loading}
          >
            Finalizar Compra
          </button>
        </div>
      </div>
    </div>
  );
};

export default CheckOut;
