import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';

const ClienteForm = ({ initialData = {}, onSave }) => {
  const [name, setName] = useState('');
  const [cuit, setCuit] = useState('');

  // Este efecto solo debe ejecutarse cuando initialData cambia
  useEffect(() => {
    // Solo actualiza el estado inicial cuando cambia initialData
    setName(initialData.name || '');
    setCuit(initialData.cuit || '');
  }, [initialData]);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Asegúrate de que el valor de name y cuit es correcto antes de enviar
    onSave({ name, cuit });
    
    // Limpia los campos después de enviar (opcional)
    setName('');
    setCuit('');
  };

  const handleNameChange = (e) => {
    // Verifica que este evento captura el valor correctamente
    setName(e.target.value);
  };

  const handleCuitChange = (e) => {
    // Verifica que este evento captura el valor correctamente
    setCuit(e.target.value);
  };

  return (
    <div>
      <h3>{initialData.id ? 'Editar Cliente' : 'Agregar Cliente'}</h3>
      <Form className='my-3' onSubmit={handleSubmit}>
        <Row className='justify-content-center'>
          <Form.Group className="mb-3 col-4">
            <Form.Label>Nombre</Form.Label>
            <Form.Control
              type='text'
              placeholder='Nombre'
              value={name}
              onChange={handleNameChange}  // Manejador de cambios para 'name'
              autoComplete='name'
              required
            />
          </Form.Group>

          <Form.Group className="mb-3 col-4">
            <Form.Label>CUIT</Form.Label>
            <Form.Control
              type='text'
              placeholder='CUIT'
              value={cuit}
              onChange={handleCuitChange}  // Manejador de cambios para 'cuit'
              autoComplete='cuit'
              required
            />
          </Form.Group>
        </Row>
        <Button className='btn-success' type="submit">Guardar</Button>
      </Form>
    </div>
  );
};

export default ClienteForm;
