// src/components/SalesReport/SalesReport.jsx
import React, { useState, useEffect } from "react";
import ApexCharts from "apexcharts";
import { Table, Form, Button, Row,FormSelect } from "react-bootstrap";
import mockSalesData from "../../mockSalesData";

const SalesReport = () => {
  const [filteredData, setFilteredData] = useState(mockSalesData);
  const [filters, setFilters] = useState({
    orderNumber: "",
    productName: "",
    category: ""
  });

  useEffect(() => {
    const chartData = filteredData.map((item) => ({
      x: item.productName,
      y: item.quantity
    }));

    const options = {
      chart: {
        type: 'bar'
      },
      series: [{
        name: 'Quantity Sold',
        data: chartData
      }],
      xaxis: {
        type: 'category'
      }
    };

    const chart = new ApexCharts(document.querySelector("#chart"), options);
    chart.render();

    return () => chart.destroy();
  }, [filteredData]);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({
      ...filters,
      [name]: value
    });
  };

  const applyFilters = () => {
    const filtered = mockSalesData.filter(item => {
      return (
        (!filters.orderNumber || item.orderNumber.toString().includes(filters.orderNumber)) &&
        (!filters.productName || item.productName.toLowerCase().includes(filters.productName.toLowerCase())) &&
        (!filters.category || item.category.toLowerCase().includes(filters.category.toLowerCase()))
      );
    });
    setFilteredData(filtered);
  };

  return (
    <div>
      <h1>Sales Report</h1>
      <Form className=" container-fluid justify-content-center" >
        <Row className=" justify-content-center">  
            <Form.Group className="col-2 " controlId="orderNumber">
            <Form.Label>Orden</Form.Label>
            <Form.Control
                type="text"
                name="orderNumber"
                value={filters.orderNumber}
                onChange={handleFilterChange}
                
            />
            </Form.Group>
            <Form.Group className="col-4" controlId="productName">
            <Form.Label>Nombre del producto</Form.Label>
            <Form.Control
                type="text"
                name="productName"
                value={filters.productName}
                onChange={handleFilterChange}
            />
            </Form.Group>
            <Form.Group className="col-4" controlId="category">
                 <Form.Label>Categoría</Form.Label>
                     <FormSelect value={filters.category} onChange={handleFilterChange}   >
                        <option value="">Seleccionar categoría</option>
                        <option value="Notebook">Notebook</option>
                        <option value="Celular">Celular</option>
                        <option value="Tablet">Tablet</option>
                    </FormSelect>
            </Form.Group>
        </Row>
        <Button onClick={applyFilters}>Apply Filters</Button>
      </Form>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Order Number</th>
            <th>Quantity</th>
            <th>Product Name</th>
            <th>Category</th>
          </tr>
        </thead>
        <tbody>
          {filteredData.map((item) => (
            <tr key={item.orderNumber}>
              <td>{item.orderNumber}</td>
              <td>{item.quantity}</td>
              <td>{item.productName}</td>
              <td>{item.category}</td>
            </tr>
          ))}
        </tbody>
      </Table>
      <div id="chart"></div>
    </div>
  );
};

export default SalesReport;
