import React, { createContext, useContext, useState, useEffect } from 'react';
import { signInWithPopup, signOut, onAuthStateChanged } from 'firebase/auth';
import { auth, googleProvider } from '../components/service/firebaseConfig';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [userRole, setUserRole] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('firebaseToken');
    if (token) {
      fetchUserRole(token);
    }

    const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
      if (currentUser) {
        setUser(currentUser);
        const firebaseToken = await currentUser.getIdToken();
        localStorage.setItem('firebaseToken', firebaseToken);
        fetchUserRole(firebaseToken);
      } else {
        setUser(null);
        setUserRole(null);
        localStorage.removeItem('firebaseToken');
      }
    });

    return () => unsubscribe();
  }, []);

  const fetchUserRole = async (firebaseToken) => {
    try {
      const response = await axios.get('http://localhost:3001/auth/user', {
        headers: {
          Authorization: `Bearer ${firebaseToken}`
        }
      });
      setUserRole(response.data.userRole);
      console.log("Rol del usuario recibido en el frontend:", response.data.userRole); 
    } catch (error) {
      console.error('Error al obtener el rol del usuario:', error);
      logout();
    }
  };
  
  

  const loginWithGoogle = async () => {
    try {
      const result = await signInWithPopup(auth, googleProvider);
      const firebaseToken = await result.user.getIdToken();
  
      const response = await axios.post('http://localhost:3001/auth/login', { firebaseToken });
      localStorage.setItem('firebaseToken', firebaseToken);
      setUser(result.user);
      setUserRole(response.data.userRole);
      console.log("Usuario autenticado:", result.user.email);
      console.log("Rol del usuario en el frontend:", response.data.userRole);
    } catch (error) {
      if (error.code === 'auth/cancelled-popup-request') {
        console.warn("Autenticación cancelada o bloqueada por el usuario.");
      } else {
        console.error('Error al iniciar sesión con Google:', error);
      }
    }
  };
  
  

  const logout = async () => {
    try {
      await signOut(auth);
      localStorage.removeItem('firebaseToken');
      setUser(null);
      setUserRole(null);
      window.location.reload(); 
    } catch (error) {
      console.error('Error al cerrar sesión:', error);
    }
  };
  

  return (
    <AuthContext.Provider value={{ user, userRole, loginWithGoogle, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
