import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Button, Form, Row, Col } from 'react-bootstrap';

const ClientesForm = ({ initialData = {} }) => {
  const [formData, setFormData] = useState({
    name: initialData.name || '',
    cuit: initialData.cuit || '',
    email: initialData.email || '',
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (!formData.name || !formData.cuit || !formData.email) {
        console.error("Todos los campos son obligatorios.");
        return;
      }
      // Asegúrate de usar la URL correcta para el servidor
      const response = await axios.post('http://localhost:3001/api/clients', formData);
      console.log('Cliente agregado:', response.data);
      navigate('/clients'); // Navega de vuelta a la lista de clientes
    } catch (error) {
      console.error('Error al agregar o actualizar el cliente:', error);
    }
  };
  

  return (
    <div className="container mt-5">
      <div className="card">
        <div className="card-header">
          <h2>{initialData.id ? 'Editar Cliente' : 'Agregar Cliente'}</h2>
        </div>
        <div className="card-body">
          <Form onSubmit={handleSubmit}>
            <Row>
              <Col md={6}>
                <Form.Group controlId="name">
                  <Form.Label>Nombre</Form.Label>
                  <Form.Control
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                  />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group controlId="cuit">
                  <Form.Label>CUIT</Form.Label>
                  <Form.Control
                    type="text"
                    name="cuit"
                    value={formData.cuit}
                    onChange={handleChange}
                    required
                  />
                </Form.Group>
              </Col>
            </Row>
            <Row className="mt-3">
              <Col md={12}>
                <Form.Group controlId="email">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                  />
                </Form.Group>
              </Col>
            </Row>
            <Button variant="primary" type="submit" className="mt-4">
              {initialData.id ? 'Actualizar Cliente' : 'Agregar Cliente'}
            </Button>
          </Form>
        </div>
      </div>
    </div>
  );
};

export default ClientesForm;
