import React, { useState, useEffect } from "react";
import ApexCharts from "apexcharts";
import { Table, Form, Button, Row, FormSelect } from "react-bootstrap";
import axios from "../service/axiosConfig";

const PurchaseReport = () => {
  const [filteredData, setFilteredData] = useState([]);
  const [suppliers, setSuppliers] = useState([]);
  const [filters, setFilters] = useState({
    orderNumber: "",
    productName: "",
    category: "",
    supplier: "",
    startDate: "",
    endDate: ""
  });

  useEffect(() => {
    fetchPurchases();
    fetchSuppliers();
  }, []);

  const fetchPurchases = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/purchases'); // Asegurar la URL completa
      if (response.data && Array.isArray(response.data)) {
        setFilteredData(response.data);
      } else {
        console.error("La respuesta no es un array válido:", response.data);
      }
    } catch (error) {
      console.error("Error obteniendo las compras:", error);
    }
  };

  const fetchSuppliers = async () => {
    try {
      const response = await axios.get("http://localhost:3001/api/suppliers");
      if (response.data && Array.isArray(response.data)) {
        setSuppliers(response.data);
      } else {
        console.error("La respuesta no es un array válido:", response.data);
      }
    } catch (error) {
      console.error("Error obteniendo los proveedores:", error);
    }
  };

  const getSupplierNameById = (supplierId) => {
    const supplier = suppliers.find(s => s.id === supplierId);
    return supplier ? supplier.name : "N/A";
  };

  useEffect(() => {
    if (filteredData.length > 0) {
      // Configuración de datos para el gráfico de barras
      const chartData = filteredData.flatMap((item) =>
        item.items ? item.items.map(orderItem => ({
          x: orderItem.product_name || "Sin nombre",
          y: orderItem.quantity
        })) : []
      );

      const options = {
        chart: {
          type: 'bar'
        },
        series: [{
          name: 'Cantidad',
          data: chartData
        }],
        xaxis: {
          type: 'category'
        }
      };

      const chart = new ApexCharts(document.querySelector("#chart"), options);
      chart.render();
    }
  }, [filteredData]);
  
  const applyFilters = () => {
    const filtered = filteredData.filter(item => {
      const itemDate = new Date(item.date);
      const startDate = filters.startDate ? new Date(filters.startDate) : null;
      const endDate = filters.endDate ? new Date(filters.endDate) : null;

      return (
        (!filters.orderNumber || item.id.toString().includes(filters.orderNumber)) &&
        (!filters.productName || item.buyer_info?.productName?.toLowerCase().includes(filters.productName.toLowerCase())) &&
        (!filters.category || getCategoryBySupplierId(item.buyer_info?.supplierId)?.toLowerCase().includes(filters.category.toLowerCase())) &&
        (!filters.supplier || getSupplierNameById(item.buyer_info?.supplierId)?.toLowerCase().includes(filters.supplier.toLowerCase())) &&
        (!startDate || itemDate >= startDate) &&
        (!endDate || itemDate <= endDate)
      );
    });
    setFilteredData(filtered);
  };

  return (
    
    <div className="container mt-4">
      <h2>Reporte de Compras</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Número de Orden</th>
            <th>Producto</th>
            <th>Categoría</th>
            <th>Proveedor</th>
            <th>Fecha</th>
            <th>Cantidad</th>
            <th>Precio</th>
            <th>Total</th>
          </tr>
        </thead>
        <tbody>
          {filteredData.map((order) => (
            order.items.map((item) => (
              <tr key={item.id}>
                <td>{order.id}</td>
                <td>{item.product_name}</td>
                <td>{item.product.category}</td>
                <td>{getSupplierNameById(item.buyer_info?.supplierId)}</td>
                <td>{new Date(order.date).toLocaleDateString()}</td>
                <td>{item.quantity}</td>
                <td>{item.price}</td>
                <td>{(item.quantity * item.price).toFixed(2)}</td>
              </tr>
            ))
          ))}
        </tbody>
      </Table>
      <div id="chart"></div>
      
    </div>
  );
};

export default PurchaseReport;
