// PurchaseForm.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Form, Button, Alert } from 'react-bootstrap';

const PurchaseForm = ({ supplierId }) => {
  // Estados para el formulario de compra
  const [purchase, setPurchase] = useState({
    productId: '',
    quantity: '',
    price: '',
    total: 0
  });
  
  // Estados para productos y categorías
  const [supplier, setSupplier] = useState(null);
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Cargar proveedor y sus productos
  useEffect(() => {
    const fetchSupplierData = async () => {
      if (!supplierId) {
        setError('ID de proveedor no proporcionado');
        setLoading(false);
        return;
      }

      try {
        // Usar las rutas correctas con /api
        const supplierResponse = await axios.get(`/api/suppliers/${supplierId}`);
        
        if (!supplierResponse.data) {
          throw new Error('No se encontró el proveedor');
        }
        
        setSupplier(supplierResponse.data);

        const productsResponse = await axios.get('/api/products', {
          params: { supplierId }
        });
        
        if (!productsResponse.data) {
          throw new Error('No se encontraron productos');
        }

        setProducts(productsResponse.data);

        // Extraer categorías únicas
        const uniqueCategories = [...new Set(productsResponse.data.map(p => p.category))];
        setCategories(uniqueCategories);
        
      } catch (error) {
        console.error('Error detallado:', error.response || error);
        setError(error.response?.data?.message || 'Error al cargar datos del proveedor');
      } finally {
        setLoading(false);
      }
    };

    fetchSupplierData();
  }, [supplierId]);
  // Filtrar productos por categoría
  const filteredProducts = selectedCategory
    ? products.filter(product => product.category === selectedCategory)
    : products;

  // Manejar cambios en el formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    setPurchase(prev => {
      const newPurchase = { ...prev, [name]: value };
      
      // Actualizar total si cambia cantidad o precio
      if (name === 'quantity' || name === 'price') {
        newPurchase.total = newPurchase.quantity * newPurchase.price;
      }
      
      return newPurchase;
    });
  };

  // Manejar envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const purchaseData = {
        ...purchase,
        supplierId,
        date: new Date().toISOString(),
        type: 'compra'
      };

      await axios.post('/purchases', purchaseData);
      navigate('/purchase-report');
    } catch (error) {
      console.error('Error:', error);
      setError('Error al procesar la compra');
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div>Cargando...</div>;
  if (error) return <Alert variant="danger">{error}</Alert>;

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group className="mb-3">
        <Form.Label>Categoría</Form.Label>
        <Form.Select
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          <option value="">Todas las categorías</option>
          {categories.map(category => (
            <option key={category} value={category}>
              {category}
            </option>
          ))}
        </Form.Select>
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Producto</Form.Label>
        <Form.Select
          name="productId"
          value={purchase.productId}
          onChange={handleChange}
          required
        >
          <option value="">Seleccione un producto</option>
          {filteredProducts.map(product => (
            <option key={product.id} value={product.id}>
              {product.title} - Stock actual: {product.stock}
            </option>
          ))}
        </Form.Select>
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Cantidad</Form.Label>
        <Form.Control
          type="number"
          name="quantity"
          value={purchase.quantity}
          onChange={handleChange}
          required
          min="1"
        />
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Precio unitario</Form.Label>
        <Form.Control
          type="number"
          name="price"
          value={purchase.price}
          onChange={handleChange}
          required
          min="0"
          step="0.01"
        />
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Total</Form.Label>
        <Form.Control
          type="number"
          value={purchase.total}
          readOnly
          disabled
        />
      </Form.Group>

      <Button type="submit" disabled={loading}>
        Realizar Compra
      </Button>
    </Form>
  );
};

export default PurchaseForm;
