import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import FormSelect from 'react-bootstrap/esm/FormSelect';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import FormGroup from 'react-bootstrap/esm/FormGroup';

const ItemForm = ({ onSave }) => {
    const [title, setTitle] = useState('');
    const [category, setCategory] = useState('');
    const [price, setPrice] = useState('');
    const [stock, setStock] = useState('');
    const [description, setDescription] = useState('');
    const navigate = useNavigate(); // Hook para navegación

    const handleSubmit = (e) => {
        e.preventDefault();
        const newProduct = { title, category, price, stock, description };
        onSave(newProduct);
        navigate('/'); 
    };

    return (
        <Form className='container' onSubmit={handleSubmit}>
            <Row className='justify-content-end col-12'>
                <FormGroup className='col-6'>
                    <Form.Label>Nombre</Form.Label>
                    <Form.Control type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                </FormGroup>
                <FormGroup className='col-6'>
                    <Form.Label>Categoría</Form.Label>
                    <FormSelect
                        value={category}
                        onChange={(e) => setCategory(e.target.value)}
                        autoComplete='category'
                        required
                    >
                        <option value="">Seleccionar categoría</option>
                        <option value="Notebook">Notebook</option>
                        <option value="Celular">Celular</option>
                        <option value="Tablet">Tablet</option>
                    </FormSelect>
                </FormGroup>
            </Row>

            <Row className='justify-content-center'>
                <FormGroup className='col-8'>
                    <Form.Label>Descripción</Form.Label>
                    <Form.Control type='text' value={description} onChange={(e) => setDescription(e.target.value)} />
                </FormGroup>
            </Row>
            <Row className='justify-content-center my-3'>
                <FormGroup className='col-4'>
                    <Form.Label>Stock</Form.Label>
                    <Form.Control type="text" value={stock} onChange={(e) => setStock(e.target.value)} />
                </FormGroup>
                <FormGroup className='col-4'>
                    <Form.Label>Precio</Form.Label>
                    <Form.Control type="text" value={price} onChange={(e) => setPrice(e.target.value)} />
                </FormGroup>
            </Row>
            <Button type="submit">Agregar Producto</Button>
        </Form>
    );
};

export default ItemForm;
