import React, { useEffect, useState } from 'react';
import axios from '../service/axiosConfig';
import Button from 'react-bootstrap/Button';
import { useNavigate } from 'react-router-dom';

const SupplierList = () => {
  const [suppliers, setSuppliers] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchSuppliers = async () => {
      try {
        const response = await axios.get('/suppliers');
        setSuppliers(response.data);
      } catch (error) {
        console.error('Error fetching suppliers:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchSuppliers();
  }, []);
  const handlePurchase = (supplierId) => {
    console.log('Navegando a compra con proveedor ID:', supplierId);
    navigate(`/form/purchase/${supplierId}`);
  };

  const handleDeleteSupplier = async (id) => {
    if (window.confirm('¿Estás seguro de que deseas eliminar este proveedor?')) {
      try {
        await axios.delete(`/suppliers/${id}`); 
        setSuppliers((prevSuppliers) =>
          prevSuppliers.filter((supplier) => supplier.id !== id)
        );
      } catch (error) {
        console.error('Error deleting supplier:', error);
        alert('Error al eliminar el proveedor. Por favor, inténtelo de nuevo.');
      }
    }
  };
  

  return (
    <div>
      <h1>Lista de Proveedores</h1>
      <Button className="btn btn-primary mb-3" onClick={() => navigate('/form/supplier')}>Agregar Proveedor</Button>
      {loading ? (
        <div>Loading...</div>
      ) : (
        suppliers.map((supplier) => (
          <div key={supplier.id}>
            <p>Nombre: {supplier.name}</p>
            <p>Teléfono: {supplier.phone}</p>
            <p>Email: {supplier.email}</p>
            <p>Categoría: {supplier.category}</p>
            <Button className="btn-warning mx-2" onClick={() => navigate(`/form/supplier/${supplier.id}`)}>Editar</Button>
            <Button className='btn-danger' onClick={() => handleDeleteSupplier(supplier.id)}>Eliminar</Button>
            <Button className='btn-primary' onClick={() => navigate(`/form/purchase/${supplier.id}`)}>Comprar Producto</Button>

            <hr />
          </div>
        ))
      )}
    </div>
  );
};

export default SupplierList;
