import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import FormGroup from 'react-bootstrap/esm/FormGroup';
import { getProductById } from '../../asyncMock';

const EditItemForm = ({ onSave }) => {
    const { itemId } = useParams();
    const [item, setItem] = useState(null);

    useEffect(() => {
        getProductById(itemId).then((data) => setItem(data));
    }, [itemId]);

    const [title, setTitle] = useState('');
    const [category, setCategory] = useState('');
    const [price, setPrice] = useState('');
    const [description, setDescription] = useState('');
    const [stock, setStock] = useState('');

    useEffect(() => {
        if (item) {
            setTitle(item.title);
            setPrice(item.price);
            setDescription(item.description);
            setStock(item.stock);
        }
    }, [item]);

    const handleSubmit = (e) => {
        e.preventDefault();
        const updatedProduct = { ...item, title, price, description, stock };
        onSave(updatedProduct);
    };

    if (!item) return <div>Loading...</div>;

    return (
        <Form className=' container ' onSubmit={handleSubmit}>
            <Row className='justify-content-end col-12'>
                <FormGroup className='col-6'>
                    <Form.Label>Nombre</Form.Label>
                    <Form.Control type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
                </FormGroup>
                
            </Row>
            <Row className='justify-content-center col-12'>
                <FormGroup className='col-8'>
                    <Form.Label>Descripci√≥n</Form.Label>
                    <Form.Control as='textarea' value={description} onChange={(e) => setDescription(e.target.value)} />
                </FormGroup>
            </Row>
            <Row className='justify-content-center col-12 my-3'>
                <FormGroup className='col-4'>
                    <Form.Label>Stock</Form.Label>
                    <Form.Control type="text" value={stock} onChange={(e) => setStock(e.target.value)} />
                </FormGroup>
                <FormGroup className='col-4'>
                    <Form.Label>Precio</Form.Label>
                    <Form.Control type="text" value={price} onChange={(e) => setPrice(e.target.value)} />
                </FormGroup>
            </Row>
            <Button type="submit">Guardar Cambios</Button>
        </Form>
    );
};

export default EditItemForm;
