import React, { useState, useEffect, useRef } from 'react';
import { collection, query, getDocs, addDoc, updateDoc, doc, deleteDoc, getDoc } from 'firebase/firestore';
import { db, auth } from '../service/firebaseConfig';
import { useNavigate } from 'react-router-dom';
import { Button } from 'primereact/button';
import { Dialog } from 'primereact/dialog';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Toast } from 'primereact/toast';

const Protected = () => {
  const [loading, setLoading] = useState(false);
  const [products, setProducts] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [productDialog, setProductDialog] = useState(false);
  const [deleteProductDialog, setDeleteProductDialog] = useState(false);
  const [deleteProductsDialog, setDeleteProductsDialog] = useState(false);
  const [product, setProduct] = useState({});
  const [submitted, setSubmitted] = useState(false);
  const [userRole, setUserRole] = useState(null);
  const toast = useRef(null);
  const navigate = useNavigate();
  const dt = useRef(null);

  const emptyProduct = {
    id: '',
    name: '',
    description: '',
    price: 0,
  };

  const fetchProductos = async () => {
    setLoading(true);
    const productsQuery = query(collection(db, 'productos'));
    const productsSnapshot = await getDocs(productsQuery);

    const prodList = productsSnapshot.docs.map(doc => ({
      ...doc.data(),
      id: doc.id
    }));

    setProducts(prodList);
    setLoading(false);
  };

  useEffect(() => {
    const verifyUser = async () => {
      const user = auth.currentUser;
      if (!user) {
        navigate('/auth/login');
      } else {
        const token = await user.getIdToken(); // Obtiene el token de Firebase
        const response = await fetch("http://localhost:3000/api/protected", {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        if (response.ok) {
          const userDoc = await getDoc(doc(db, 'users', user.uid));
          setUserRole(userDoc?.data()?.role || 'user');
          if (userDoc.data()?.role === 'admin') {
            fetchProductos();
          } else {
            navigate('/'); // Redirige si no es admin
          }
        } else {
          console.error("Error al verificar el token");
          navigate('/auth/login');
        }
      }
    };

    verifyUser();
  }, [userRole, navigate]);

  const formatCurrency = (value) => {
    return value.toLocaleString('en-US', { style: 'currency', currency: 'USD' });
  };

  const openNew = () => {
    setProduct(emptyProduct);
    setSubmitted(false);
    setProductDialog(true);
  };

  const hideDialog = () => {
    setSubmitted(false);
    setProductDialog(false);
  };

  const hideDeleteProductDialog = () => {
    setDeleteProductDialog(false);
  };

  const hideDeleteProductsDialog = () => {
    setDeleteProductsDialog(false);
  };

  const saveProduct = async () => {
    setSubmitted(true);
    setLoading(true);
    setProductDialog(false);
    if (!product.id) {
      await addDoc(collection(db, 'productos'), product);
    } else {
      await updateDoc(doc(db, 'productos', product.id), product);
    }

    toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Product Created', life: 3000 });
    setLoading(false);
    fetchProductos();
  };

  const editProduct = (product) => {
    setProduct({ ...product });
    setProductDialog(true);
  };

  const confirmDeleteProduct = (product) => {
    setProduct(product);
    setDeleteProductDialog(true);
  };

  const deleteProduct = async () => {
    setLoading(true);
    let _products = products.filter((val) => val.id !== product.id);

    setProducts(_products);
    setDeleteProductDialog(false);
    setProduct(emptyProduct);

    await deleteDoc(doc(db, 'productos', product.id));

    toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Product Deleted', life: 3000 });
    setLoading(false);
    fetchProductos();
  };

  const exportCSV = () => {
    dt.current.exportCSV();
  };

  useEffect(() => {
    const verifyUser = async () => {
      const user = auth.currentUser;
      if (!user) {
        navigate('/auth/login'); 
      } else {
        const token = await user.getIdToken(); 
        const response = await fetch("http://localhost:3000/api/protected", {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        if (response.ok) {
          const userDoc = await getDoc(doc(db, 'users', user.uid));
          setUserRole(userDoc?.data()?.role || 'user');
          if (userDoc.data()?.role === 'admin') {
            fetchProductos();
          } else {
            navigate('/'); 
          }
        } else {
          console.error("Error al verificar el token");
          navigate('/auth/login');
        }
      }
    };
  
    verifyUser();
  }, [userRole, navigate]);
  


  return (
    <div>
      <Toast ref={toast} />
      <div className="card">
        <DataTable ref={dt} value={products} selection={selectedProducts} onSelectionChange={(e) => setSelectedProducts(e.value)} dataKey="id" paginator rows={10} rowsPerPageOptions={[5, 10, 25]}>
          <Column selectionMode="multiple" headerStyle={{ width: '3em' }}></Column>
          <Column field="name" header="Name" sortable></Column>
          <Column field="description" header="Description" sortable></Column>
          <Column field="price" header="Price" body={(rowData) => formatCurrency(rowData.price)} sortable></Column>
          <Column body={(rowData) => (
            <div>
              <Button icon="pi pi-pencil" className="p-button-rounded p-button-success mr-2" onClick={() => editProduct(rowData)} />
              <Button icon="pi pi-trash" className="p-button-rounded p-button-warning" onClick={() => confirmDeleteProduct(rowData)} />
            </div>
          )}></Column>
        </DataTable>
        <Button label="New" icon="pi pi-plus" className="p-button-success mr-2" onClick={openNew} />
        <Button label="Delete" icon="pi pi-trash" className="p-button-danger" onClick={() => setDeleteProductsDialog(true)} disabled={!selectedProducts || !selectedProducts.length} />
        <Button label="Export" icon="pi pi-upload" className="p-button-help" onClick={exportCSV} />
      </div>
    </div>
  );
};

export default Protected;
