import React from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, db } from '../service/firebaseConfig'; // Asegúrate de que firebaseConfig esté configurado correctamente
import { signInWithPopup, GoogleAuthProvider, signOut } from 'firebase/auth';
import { doc, getDoc, setDoc } from 'firebase/firestore';

const Auth = ({ setUserRole }) => {
  const navigate = useNavigate();
  const provider = new GoogleAuthProvider();

  // Función para manejar el inicio de sesión con Google
  const handleLogin = async () => {
    try {
      // Inicia sesión con Google
      const result = await signInWithPopup(auth, provider);
      const user = result.user;
      
      // Obtiene el token de Firebase después de iniciar sesión
      const firebaseToken = await user.getIdToken(); 

      // Envía el token al backend
      const response = await fetch('http://localhost:3000/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ firebaseToken }),
      });

      const data = await response.json();
      if (data.ok) {
        // Si el inicio de sesión en el backend fue exitoso, actualiza el rol del usuario
        const userRole = data.userRole || 'user';
        setUserRole(userRole);

        // Redirige a la página protegida
        navigate('/protected');
      } else {
        console.error('Error al iniciar sesión en el backend:', data.message);
      }
    } catch (error) {
      console.error('Error en el inicio de sesión con Google:', error);
    }
  };

  // Función para cerrar sesión
  const handleLogout = async () => {
    try {
      await signOut(auth); // Cierra la sesión en Firebase
      setUserRole(null); // Resetea el rol de usuario
      navigate('/auth/login'); // Redirige al usuario a la página de login
    } catch (error) {
      console.error('Error al cerrar sesión:', error);
    }
  };

  return (
    <div className="login-container">
      <h1>Login</h1>
      <button className='btn btn-bd-accent' onClick={handleLogin}>Iniciar sesión con Google</button>
      <button className='btn btn-light' onClick={handleLogout}>Cerrar sesión</button>
    </div>
  );
};

export default Auth;
