import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';

const ClienteForm = ({ initialData = {}, onSave }) => {
  const [name, setName] = useState('');
  const [cuit, setCuit] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    setName(initialData.name || '');
    setCuit(initialData.cuit || '');
  }, [initialData]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

  
    if (cuit.length !== 11 || isNaN(cuit)) {
      setError('CUIT debe tener 11 dígitos numéricos');
      setLoading(false);
      return;
    }


    onSave({ name, cuit })
      .then(() => {
        setName('');
        setCuit('');
      })
      .catch((err) => {
        setError('Error al guardar el cliente');
      })
      .finally(() => {
        setLoading(false);
      });
  };

  return (
    <div>
      <h3>{initialData.id ? 'Editar Cliente' : 'Agregar Cliente'}</h3>
      {error && <p className="text-danger">{error}</p>}
      <Form className='my-3' onSubmit={handleSubmit}>
        <Row className='justify-content-center'>
          <Form.Group className="mb-3 col-4">
            <Form.Label>Nombre</Form.Label>
            <Form.Control
              type='text'
              placeholder='Nombre'
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
              disabled={loading}
            />
          </Form.Group>

          <Form.Group className="mb-3 col-4">
            <Form.Label>CUIT</Form.Label>
            <Form.Control
              type='text'
              placeholder='CUIT'
              value={cuit}
              onChange={(e) => setCuit(e.target.value)}
              autoComplete='cuit'
              required
              disabled={loading}
            />
          </Form.Group>
        </Row>
        <Button className='btn-success' type="submit" disabled={loading}>
          {loading ? 'Guardando...' : 'Guardar'}
        </Button>
      </Form>
    </div>
  );
};

export default ClienteForm;
