import React, { useState, useEffect } from "react";
import ApexCharts from "apexcharts";
import { Table, Form, Button, Row, FormSelect } from "react-bootstrap";

const SalesReport = () => {
  const [salesData, setSalesData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [filters, setFilters] = useState({
    orderNumber: "",
    productName: "",
    category: ""
  });

  useEffect(() => {
    // Load sales data from local storage
    const storedSalesData = JSON.parse(localStorage.getItem("salesData")) || [];
    setSalesData(storedSalesData);
    setFilteredData(storedSalesData);
  }, []);

  useEffect(() => {
    const chartData = filteredData.map((item) => ({
      x: item.productName,
      y: item.quantity
    }));

    const options = {
      chart: {
        type: 'donut'
      },
      series: [{
        name: 'Cantidad Vendida',
        data: chartData
      }],
      labels: filteredData.map(item => item.productName)
    };

    const chart = new ApexCharts(document.querySelector("#chart"), options);
    chart.render();

    return () => chart.destroy();
  }, [filteredData]);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({
      ...filters,
      [name]: value
    });
  };

  const handleClearFilters = () => {
    setFilters({ orderNumber: "", productName: "", category: "" });
    setFilteredData(salesData);  // Reset to all data
  };

  const applyFilters = () => {
    const filtered = salesData.filter(item => {
      return (
        (!filters.orderNumber || item.orderNumber.toString().includes(filters.orderNumber)) &&
        (!filters.productName || item.productName.toLowerCase().includes(filters.productName.toLowerCase())) &&
        (!filters.category || item.category.toLowerCase().includes(filters.category.toLowerCase()))
      );
    });
    setFilteredData(filtered);
  };

  return (
    <div className=" justify-content-center container-fluid">
      <h1>Reporte de Ventas</h1>
      <Form className=" container-fluid justify-content-center my-4" >
        <Row className=" justify-content-center">  
          <Form.Group className="col-2 " controlId="orderNumber">
            <Form.Label>Orden</Form.Label>
            <Form.Control
              type="text"
              name="orderNumber"
              value={filters.orderNumber}
              onChange={handleFilterChange}
            />
          </Form.Group>
          <Form.Group className="col-4" controlId="productName">
            <Form.Label>Nombre del producto</Form.Label>
            <Form.Control
              type="text"
              name="productName"
              value={filters.productName}
              onChange={handleFilterChange}
            />
          </Form.Group>
          <Form.Group className="col-4" controlId="category">
            <Form.Label>Categoría</Form.Label>
            <FormSelect 
              value={filters.category} 
              onChange={handleFilterChange} 
              name="category" // Ensure the correct name attribute
            >
              <option value="">Seleccionar categoría</option>
              <option value="Notebook">Notebook</option>
              <option value="Celular">Celular</option>
              <option value="Tablet">Tablet</option>
            </FormSelect>
          </Form.Group>
        </Row>
        <Button className="mt-4" onClick={applyFilters}>Filtrar</Button>
        <Button className="mt-4 mx-3 btn btn-secondary" onClick={handleClearFilters}>Borrar Filtros</Button>
      </Form>
      <Table className=" table-responsive table w-75 p-5 m-5" striped bordered hover>
        <thead>
          <tr>
            <th>Orden</th>
            <th>Cantidad</th>
            <th>Producto</th>
            <th>Categoría</th>
          </tr>
        </thead>
        <tbody>
          {filteredData.map((item) => (
            <tr key={item.orderNumber}>
              <td>{item.orderNumber}</td>
              <td>{item.quantity}</td>
              <td>{item.productName}</td>
              <td>{item.category}</td>
            </tr>
          ))}
        </tbody>
      </Table>
      <div className="" id="chart">
      </div>
    </div>
  );
};

export default SalesReport;
