import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth } from '../service/firebaseConfig';
import { useAuth } from '../../context/AuthContext';

const Auth = ({ setUserRole }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const { setUser } = useAuth();

  const handleLogin = async () => {
    try {
      const userCredential = await auth.signInWithEmailAndPassword(email, password);
      const user = userCredential.user;
      const token = await user.getIdToken();

      localStorage.setItem('firebaseToken', token);
      setUser(user); // Actualiza el usuario en el contexto

      // Determina el rol y redirige
      const isAdmin = user.email === 'admin@example.com'; // Cambia esto a tu email de administrador
      setUserRole(isAdmin ? 'admin' : 'user');

      navigate('/'); // Redirige al home
    } catch (error) {
      console.error('Error en inicio de sesión:', error);
      alert('Error en el inicio de sesión. Revisa tus credenciales.');
    }
  };

  return (
    <div>
      <h2>Iniciar sesión</h2>
      <input
        type="email"
        placeholder="Correo electrónico"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Contraseña"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Iniciar sesión</button>
    </div>
  );
};

export default Auth;
