import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Button from 'react-bootstrap/Button';
import ProductForm from '../ProductForm/ProductForm';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editProduct, setEditProduct] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      try {
        const response = await axios.get('http://localhost:3000/api/productos');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  const handleAddProduct = async (product) => {
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:3000/api/productos', product);
      setProducts((prevProducts) => [...prevProducts, response.data]);
    } catch (error) {
      console.error('Error adding product:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateProduct = async (id, updatedProduct) => {
    setLoading(true);
    try {
      await axios.put(`http://localhost:3000/api/productos/${id}`, updatedProduct);
      setProducts((prevProducts) =>
        prevProducts.map((product) =>
          product.id === id ? { ...product, ...updatedProduct } : product
        )
      );
    } catch (error) {
      console.error('Error updating product:', error);
    } finally {
      setLoading(false);
      setEditProduct(null);
    }
  };

  const handleDeleteProduct = async (id) => {
    setLoading(true);
    try {
      await axios.delete(`http://localhost:3000/api/productos/${id}`);
      setProducts((prevProducts) =>
        prevProducts.filter((product) => product.id !== id)
      );
    } catch (error) {
      console.error('Error deleting product:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Lista de Productos</h1>

      {products.map((product) => (
        <div key={product.id}>
          <p>Nombre: {product.title}</p>
          <p>Descripci√≥n: {product.description}</p>
          <p>Precio: {product.price}</p>
          <p>Precio de Compra: {product.purchase_price}</p>
          <p>Stock: {product.stock}</p>
          <Button className='mx-2' onClick={() => setEditProduct(product)}>Editar</Button>
          <Button onClick={() => handleDeleteProduct(product.id)}>Eliminar</Button>
          <hr />
        </div>
      ))}
      {editProduct && (
        <ProductForm
          initialData={editProduct}
          onSave={(updatedProduct) => handleUpdateProduct(editProduct.id, updatedProduct)}
        />
      )}
      <ProductForm onSave={handleAddProduct} />
    </div>
  );
};

export default ProductList;
