import React, { useState, useEffect } from 'react';
import axios from '../service/axiosConfig';
import { useParams, useNavigate } from 'react-router-dom';

const ProductForm = ({ initialData = {}, onSave }) => {
  const [product, setProduct] = useState(initialData);
  const [loading, setLoading] = useState(false);
  const { id } = useParams(); // Para obtener el ID si estamos editando un producto existente
  const navigate = useNavigate();

  useEffect(() => {
    if (id) {
      // Si hay un ID, estamos en modo edición: cargar datos del producto
      fetchProductById(id);
    }
  }, [id]);

  const fetchProductById = async (productId) => {
    try {
      setLoading(true);
      const response = await axios.get(`/products/${productId}`);
      setProduct(response.data);
    } catch (error) {
      console.error('Error al obtener el producto:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct((prevProduct) => ({
      ...prevProduct,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (id) {
        // Actualización de producto existente
        const response = await axios.put(`/products/${id}`, product);
        console.log('Producto actualizado:', response.data);
        onSave && onSave(response.data);
      } else {
        // Creación de nuevo producto
        const response = await axios.post('/products', product);
        console.log('Producto creado:', response.data);
        onSave && onSave(response.data);
      }
      navigate('/products'); // Navegar de vuelta a la lista de productos
    } catch (error) {
      console.error('Error al agregar o actualizar el producto:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="product-form">
      <div className="form-group">
        <label htmlFor="title">Nombre del Producto:</label>
        <input
          type="text"
          id="title"
          name="title"
          value={product.title || ''}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="description">Descripción:</label>
        <textarea
          id="description"
          name="description"
          value={product.description || ''}
          onChange={handleChange}
        />
      </div>
      <div className="form-group">
        <label htmlFor="price">Precio:</label>
        <input
          type="number"
          id="price"
          name="price"
          value={product.price || ''}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="purchasePrice">Precio de Compra:</label>
        <input
          type="number"
          id="purchasePrice"
          name="purchasePrice"
          value={product.purchasePrice || ''}
          onChange={handleChange}
        />
      </div>
      <div className="form-group">
        <label htmlFor="img">URL de Imagen:</label>
        <input
          type="text"
          id="img"
          name="img"
          value={product.img || ''}
          onChange={handleChange}
        />
      </div>
      <button type="submit" className="btn btn-primary" disabled={loading}>
        {loading ? 'Guardando...' : id ? 'Actualizar Producto' : 'Agregar Producto'}
      </button>
    </form>
  );
};

export default ProductForm;
