import React, { useState, useEffect } from 'react';
import axios from '../service/axiosConfig';
import { useNavigate } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Container from 'react-bootstrap/Container';

const PurchaseForm = ({ supplierId, onSave }) => {
  const [supplier, setSupplier] = useState(null);
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState('');
  const [quantity, setQuantity] = useState(0);
  const [price, setPrice] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    if (supplierId) {
      fetchSupplier(supplierId);
    }
  }, [supplierId]);

  const fetchSupplier = async (id) => {
    try {
      const response = await axios.get(`/suppliers/${id}`);
      setSupplier(response.data);

      // Fetch products from this supplier's category
      const productsResponse = await axios.get(`/products?category=${response.data.category}`);
      setProducts(productsResponse.data);
    } catch (error) {
      console.error('Error fetching supplier or products:', error);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectedProduct) {
      onSave({
        supplierId,
        productId: selectedProduct,
        quantity,
        price,
      });
    } else {
      navigate(`/form/product/new?supplierId=${supplierId}&quantity=${quantity}&price=${price}`);
    }
  };

  return (
    <Container>
      <h2 className="my-4">Compra de Productos - Proveedor: {supplier?.name}</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3">
          <Form.Label>Producto</Form.Label>
          <Form.Select
            value={selectedProduct}
            onChange={(e) => {
              const productId = e.target.value;
              setSelectedProduct(productId);
              const product = products.find((p) => p.id === parseInt(productId, 10));
              setPrice(product?.price || 0);
            }}
          >
            <option value="">Selecciona un Producto</option>
            {products.map((product) => (
              <option key={product.id} value={product.id}>
                {product.title}
              </option>
            ))}
          </Form.Select>
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Cantidad</Form.Label>
          <Form.Control
            type="number"
            min="1"
            value={quantity}
            onChange={(e) => setQuantity(Number(e.target.value))}
            required
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Precio</Form.Label>
          <Form.Control
            type="number"
            value={price}
            onChange={(e) => setPrice(Number(e.target.value))}
            required
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          {selectedProduct ? 'Guardar Compra' : 'Agregar Nuevo Producto y Comprar'}
        </Button>
      </Form>
    </Container>
  );
};

export default PurchaseForm;
