import './App.css';
import 'bootstrap';
import NavBar from './components/NavBar/NavBar';
import ItemListContainer from './components/ItemListContainer/ItemListContainer';
import ItemDetailContainer from './components/ItemDetailContainer/ItemDetailContainer';
import { Routes, Route, Link, useNavigate } from 'react-router-dom';
import { Contact } from './views/Contact';
import { Cart } from './views/Cart';
import CheckOut from './views/CheckOut';
import SupplierList from './components/SupplierList/SupplierList';
import ItemForm from './components/ItemForm/ItemForm';
import EditItemForm from './components/EditItemForm/EditItemForm';
import { useState, useEffect } from 'react';
import { getProduct } from './asyncMock';

function App() {
  const [items, setItems] = useState([]);
  const navigate = useNavigate(); // Hook para navegaci√≥n

  useEffect(() => {
    getProduct().then((products) => setItems(products));
  }, []);

  const handleEditProduct = (updatedProduct) => {
    setItems((prevItems) =>
      prevItems.map((item) =>
        item.id === updatedProduct.id ? updatedProduct : item
      )
    );
  };

  const handleAddProduct = (newProduct) => {
    setItems((prevItems) => [...prevItems, newProduct]);
    navigate('/'); // Redirige a la p√°gina principal despu√©s de agregar un producto
  };

  return (
    <div className="App">
      <NavBar/>

      <Routes>
        <Route path="/" element={<ItemListContainer greeting={"Bienvenidos"}/>}/>
        <Route path="/category/:categoryId" element={<ItemListContainer />}/>
        <Route path="/item/:itemId" element={<ItemDetailContainer/>}/>
        <Route path='/contact' element={<Contact/>}/>
        <Route path="/cart" element={<Cart/>}/>
        <Route path="/suppliers" element={<SupplierList/>}/>
        <Route path="/checkout" element={<CheckOut/>}/>
        <Route path="/editItem/:itemId" element={<EditItemForm onSave={handleEditProduct} />} />
        <Route path="/newItem" element={<ItemForm onSave={handleAddProduct} />} />
        <Route path="*" element={<h1>404 NOT FOUND</h1>}/>
      </Routes>

      <Link to={"/"}> 
        <button className="my-xxl-5 justify-content-end btn btn-outline-success">üè† HOME</button>
      </Link>
    </div>
  );
}

export default App;
