import React, { useEffect, useState } from 'react';
import axios from '../service/axiosConfig';
import Item from '../Item/Item';
import { useNavigate } from 'react-router-dom';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editProduct, setEditProduct] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/products');
      console.log("Productos obtenidos:", response.data);
      setProducts(response.data);
    } catch (error) {
      console.error('Error al obtener los productos:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteProduct = async (id) => {
    try {
      console.log('Intentando eliminar producto con ID:', id);
      await axios.delete(`/products/${id}`);
      setProducts((prevProducts) => prevProducts.filter((product) => product.id !== id));
    } catch (error) {
      console.error('Error al eliminar el producto:', error);
      alert('Error al eliminar el producto. Por favor, inténtalo de nuevo.');
    }
  };

  const handleEditProduct = (id) => {
    const productToEdit = products.find((product) => product.id === id);
    setEditProduct(productToEdit);
    navigate(`/form/product/${id}`);
  };

  const navigateToForm = (type) => {
    navigate(`/form/${type}`);
  };

const ProductUpdateButton = ({ userEmail, products, incompleteProfile }) => {
  const handleSendEmail = async () => {
    try {
      await axios.post('http://localhost:3001/api/email/sendProductsUpdate', {
        userEmail,
        products,
        incompleteProfile,
      });
      alert('Correo enviado con éxito');
    } catch (error) {
      console.error('Error al enviar el correo:', error);
      alert('Hubo un error al enviar el correo');
    }
  };

  return (
    <button onClick={handleSendEmail} className="btn btn-primary">
      Enviar actualización de productos
    </button>
  );
};
  return (
    <div className="product-list-container">
      
      <h2>Lista de Productos</h2>
      <div className="form-selection-buttons">
        <button onClick={() => navigateToForm("product")} className="btn btn-primary m-2">Agregar Producto</button>
        <button onClick={handleSendEmail} className="btn btn-primary">
      Enviar actualización de productos
    </button>
      </div>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div className="d-flex flex-wrap justify-content-around">
          {products.length > 0 ? (
            products.map((product) => (
              <Item
                key={product.id}
                id={product.id}
                title={product.title}
                img={product.img}
                price={product.price}
                purchasePrice={product.purchasePrice}
                description={product.description}
                showEditButton={true}
                showDeleteButton={true}
                onEdit={() => handleEditProduct(product.id)}
                onDelete={handleDeleteProduct}
                className="product-item"
              />
            ))
          ) : (
            <p>No hay productos disponibles.</p>
          )}
        </div>
      )}
      
      
    </div>
  );
};

export default ProductList;
