import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, db } from '../service/firebaseConfig';
import { signInWithPopup, GoogleAuthProvider, signOut } from 'firebase/auth';
import { doc, getDoc, setDoc } from 'firebase/firestore';

const Auth = ({ setUserRole }) => {
  const navigate = useNavigate();
  const provider = new GoogleAuthProvider();

  const handleLogin = async () => {
    try {
   
      const result = await signInWithPopup(auth, provider);
      const user = result.user;
      const userRef = doc(db, 'users', user.uid); 
      const userSnap = await getDoc(userRef); 
      if (!userSnap.exists()) {
        await setDoc(userRef, { role: 'user', email: user.email, name: user.displayName });
      }

      const userRole = (userSnap.exists() && userSnap.data().role) || 'user';
      setUserRole(userRole); 
      navigate(userRole === 'admin' ? '/protected' : '/'); 
    } catch (error) {
      console.error('Error en el inicio de sesión con Google:', error);
    }
  };

  const handleLogout = async () => {
    try {
   
      await signOut(auth);
      setUserRole(null); 
      navigate('/auth/login'); 
    } catch (error) {
      console.error('Error al cerrar sesión:', error);
    }
  };

  useEffect(() => {
    
    const unsubscribe = auth.onAuthStateChanged(async (user) => {
      if (user) {
        const userRef = doc(db, 'users', user.uid);
        const userSnap = await getDoc(userRef);
        setUserRole(userSnap.data()?.role || 'user');
      } else {
        setUserRole(null);
      }
    });

    return () => unsubscribe();
  }, [setUserRole]);

  return (
    <div className="login-container">
      <h1>Login</h1>
      <button className="btn btn-bd-accent" onClick={handleLogin}>Iniciar sesión con Google</button>
      <button className="btn btn-light" onClick={handleLogout}>Cerrar sesión</button>
    </div>
  );
};

export default Auth;
