import React, { useState, useEffect } from 'react';
import axios from '../service/axiosConfig';
import { Form, Button, Container, Row, Col } from 'react-bootstrap';

const PurchaseForm = ({ supplierId, onSave }) => {
  const [supplier, setSupplier] = useState(null);
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [quantity, setQuantity] = useState(0);
  const [price, setPrice] = useState(0);

  useEffect(() => {
    const fetchSupplier = async () => {
      try {
        // Fetch supplier details
        const supplierResponse = await axios.get(`/api/suppliers/${supplierId}`);
        setSupplier(supplierResponse.data);

        // Fetch products from this supplier's category once the supplier is loaded
        if (supplierResponse.data?.category) {
          const productsResponse = await axios.get(`/api/products?category=${supplierResponse.data.category}`);
          setProducts(productsResponse.data);
        }
      } catch (error) {
        console.error('Error fetching supplier or products:', error);
      }
    };

    if (supplierId) {
      fetchSupplier();
    }
  }, [supplierId]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectedProduct) {
      onSave({
        supplierId,
        productId: selectedProduct.id,
        quantity,
        price,
      });
    } else {
      console.error("Debe seleccionar un producto para continuar");
    }
  };

  return (
    <Container>
      <h2 className="my-4">Compra de Productos - Proveedor: {supplier?.name}</h2>
      <Form onSubmit={handleSubmit}>
        <Row className="mb-3">
          <Form.Group as={Col} controlId="formProduct">
            <Form.Label>Producto</Form.Label>
            <Form.Select
              value={selectedProduct?.id || ''}
              onChange={(e) => {
                const product = products.find(p => p.id.toString() === e.target.value);
                setSelectedProduct(product);
                setPrice(product?.price || 0);
              }}
            >
              <option value="">Selecciona un Producto</option>
              {products.map((product) => (
                <option key={product.id} value={product.id}>
                  {product.title}
                </option>
              ))}
            </Form.Select>
          </Form.Group>
        </Row>

        <Row className="mb-3">
          <Form.Group as={Col} controlId="formQuantity">
            <Form.Label>Cantidad</Form.Label>
            <Form.Control
              type="number"
              value={quantity}
              onChange={(e) => setQuantity(Number(e.target.value))}
              min="1"
              required
            />
          </Form.Group>

          <Form.Group as={Col} controlId="formPrice">
            <Form.Label>Precio</Form.Label>
            <Form.Control
              type="number"
              value={price}
              onChange={(e) => setPrice(Number(e.target.value))}
              min="0.01"
              step="0.01"
              required
            />
          </Form.Group>
        </Row>

        <Button variant="primary" type="submit">
          Guardar Compra
        </Button>
      </Form>
    </Container>
  );
};

export default PurchaseForm;
