import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';

const ProductForm = ({ initialData = {}, onSave, onCancel }) => {
  const [productData, setProductData] = useState({
    name: '',
    description: '',
    stock: 0,
    price: 0,
    purchase_price: 0,
    category: '',
    supplier_id: '',
    img: ''
  });

  useEffect(() => {
    if (initialData) {
      setProductData(initialData);
    }
  }, [initialData]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProductData({
      ...productData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(productData); // Guarda el producto
    resetForm();
  };

  const resetForm = () => {
    setProductData({
      name: '',
      description: '',
      stock: 0,
      price: 0,
      purchase_price: 0,
      category: '',
      supplier_id: '',
      img: ''
    });
    if (onCancel) {
      onCancel(); // Llama a la funci칩n onCancel si est치 definida
    }
  };

  return (
    <Form className='my-3' onSubmit={handleSubmit}>
      <Row className='justify-content-center'>
        <Form.Group className="mb-3 col-4">
          <Form.Label htmlFor="product-name">Nombre</Form.Label>
          <Form.Control
            type='text'
            id="product-name"
            name="name"
            placeholder='Nombre'
            value={productData.name}
            onChange={handleChange}
            required
          />
        </Form.Group>

        {/* Resto de los campos */}
        <Button className='btn-primary' type="submit">
          {initialData.id ? 'Actualizar Producto' : 'Agregar Producto'}
        </Button>
        {initialData.id && (
          <Button className='btn-secondary ml-2' onClick={resetForm}>
            Cancelar
          </Button>
        )}
      </Row>
    </Form>
  );
};

export default ProductForm;
